import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  IconButton,
  Typography,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Tooltip,
  Select,
  MenuItem,
  Button,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  ClickAwayListener,
  Collapse,
  Slide,
} from '@mui/material';
import {
  DragHandle as DragIcon,
  PlayArrow as PlayIcon,
  Delete as DeleteIcon,
  Send as SendIcon,
  Add as AddIcon,
  Settings as SettingsIcon,
  Info as InfoIcon,
  ExpandMore as ExpandMoreIcon,
  ExpandLess as ExpandLessIcon,
} from '@mui/icons-material';
import { processWithAssistant, generateSummary } from '../utils/brainstormUtils';

// Model configuration presets
const MODEL_PRESETS = {
  creative: {
    name: "Creative",
    config: {
      temperature: 0.9,
      max_tokens: 2000,
      top_p: 0.95,
      frequency_penalty: 0.5,
      presence_penalty: 0.5
    }
  },
  balanced: {
    name: "Balanced",
    config: {
      temperature: 0.7,
      max_tokens: 2000,
      top_p: 0.9,
      frequency_penalty: 0.2,
      presence_penalty: 0.2
    }
  },
  precise: {
    name: "Precise",
    config: {
      temperature: 0.3,
      max_tokens: 2000,
      top_p: 0.8,
      frequency_penalty: 0,
      presence_penalty: 0
    }
  }
};

const BrainstormControl = ({ panels, onSubmitToPanels, currentMessage }) => {
  const [sequence, setSequence] = useState([]);
  const [selectedOutputPanel, setSelectedOutputPanel] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [results, setResults] = useState(null);
  const [availableModels, setAvailableModels] = useState([]);
  const [modelSettingsOpen, setModelSettingsOpen] = useState(false);
  const [selectedPanelForSettings, setSelectedPanelForSettings] = useState(null);
  const [isExpanded, setIsExpanded] = useState(true);

  // Add a panel to the sequence with default preset
  const handleAddToSequence = (panelId) => {
    setSequence(prev => [...prev, {
      id: `seq-${Date.now()}`,
      panelId,
      modelId: availableModels[0]?.id || 'default',
      preset: 'balanced',
      config: { ...MODEL_PRESETS.balanced.config }
    }]);
  };

  // Display current configuration
  const renderConfigInfo = (item) => {
    const preset = item.preset || 'balanced';
    return (
      <Box sx={{ display: 'flex', alignItems: 'center', ml: 1 }}>
        <Tooltip title={
          <Box>
            <Typography variant="body2">Preset: {MODEL_PRESETS[preset].name}</Typography>
          </Box>
        }>
          <IconButton size="medium" color="primary">
            <InfoIcon />
          </IconButton>
        </Tooltip>
      </Box>
    );
  };

  // Remove a panel from the sequence
  const handleRemoveFromSequence = (sequenceId) => {
    setSequence(prev => prev.filter(item => item.id !== sequenceId));
  };

  // Open model settings dialog
  const handleOpenModelSettings = (sequenceItem) => {
    setSelectedPanelForSettings(sequenceItem);
    setModelSettingsOpen(true);
  };

  // Update model for a panel
  const handleModelChange = (modelId) => {
    setSequence(prev => prev.map(item => 
      item.id === selectedPanelForSettings.id
        ? { ...item, modelId }
        : item
    ));
    setModelSettingsOpen(false);
  };

  // Fetch available models on component mount
  useEffect(() => {
    fetchAvailableModels();
  }, []);

  const fetchAvailableModels = async () => {
    try {
      const response = await fetch('/api/models');
      const data = await response.json();
      if (data.status === 'online') {
        setAvailableModels(data.models);
      }
    } catch (error) {
      console.error('Error fetching models:', error);
    }
  };

  // Start the brainstorming process
  const handleStartBrainstorm = async () => {
    if (sequence.length === 0 || !currentMessage) return;
    setIsProcessing(true);
    
    try {
      const results = [];
      for (const item of sequence) {
        const response = await processWithAssistant(item.panelId, currentMessage, item.modelId);
        results.push({
          panelId: item.panelId,
          response
        });
      }
      
      const summary = generateSummary(results);
      setResults(summary);
      
      if (selectedOutputPanel) {
        onSubmitToPanels(selectedOutputPanel, summary);
      }
    } catch (error) {
      console.error('Brainstorm error:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleClickAway = () => {
    if (isExpanded && !modelSettingsOpen) {
      setIsExpanded(false);
    }
  };

  const toggleExpanded = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <ClickAwayListener onClickAway={handleClickAway}>
      <Box sx={{ position: 'relative', mb: isExpanded ? 2 : 0 }}>
        <Slide direction="down" in={!isExpanded} mountOnEnter unmountOnExit>
          <Paper 
            sx={{ 
              p: 1, 
              display: 'flex', 
              alignItems: 'center',
              cursor: 'pointer',
              '&:hover': {
                bgcolor: 'action.hover',
              }
            }}
            onClick={toggleExpanded}
          >
            <Typography variant="subtitle1" sx={{ flex: 1 }}>
              AI Brainstorm Control
            </Typography>
            <ExpandMoreIcon />
          </Paper>
        </Slide>

        <Collapse in={isExpanded} timeout="auto">
          <Paper 
            sx={{ 
              p: 2, 
              m: 2,
              position: 'relative',
            }}
          >
            <Box sx={{ 
              position: 'absolute', 
              top: 8, 
              right: 8, 
              zIndex: 1,
              cursor: 'pointer',
            }}
              onClick={toggleExpanded}
            >
              <Tooltip title="Collapse">
                <ExpandLessIcon />
              </Tooltip>
            </Box>

            <Typography variant="h6" gutterBottom>
              AI Brainstorm Control
            </Typography>
            
            {/* Sequence Builder */}
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1" gutterBottom>
                Processing Sequence
              </Typography>
              <List>
                {sequence.map((item, index) => (
                  <ListItem
                    key={item.id}
                    sx={{ 
                      bgcolor: 'background.paper', 
                      mb: 1,
                      borderRadius: 1,
                      border: '1px solid',
                      borderColor: 'divider',
                      '&:hover': {
                        bgcolor: 'action.hover',
                      }
                    }}
                  >
                    <DragIcon sx={{ mr: 1, color: 'action.active' }} />
                    <ListItemText 
                      primary={
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <Typography variant="subtitle2">
                            Panel {panels.findIndex(p => p.id === item.panelId) + 1}
                          </Typography>
                          {renderConfigInfo(item)}
                        </Box>
                      }
                      secondary={`Model: ${item.modelId} - ${MODEL_PRESETS[item.preset || 'balanced'].name}`}
                    />
                    <ListItemSecondaryAction sx={{ display: 'flex', gap: 1 }}>
                      <Tooltip title="Model Settings">
                        <IconButton 
                          onClick={(e) => { e.stopPropagation(); handleOpenModelSettings(item); }}
                          color="primary"
                          size="medium"
                        >
                          <SettingsIcon />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Remove Panel">
                        <IconButton 
                          onClick={(e) => { e.stopPropagation(); handleRemoveFromSequence(item.id); }}
                          color="error"
                          size="medium"
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Tooltip>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
              </List>

              {/* Add Panel to Sequence */}
              <Box sx={{ mt: 2 }}>
                {panels.map((panel, index) => (
                  <Tooltip key={panel.id} title="Add to sequence">
                    <Chip
                      label={`Panel ${index + 1}`}
                      onClick={(e) => { e.stopPropagation(); handleAddToSequence(panel.id); }}
                      icon={<AddIcon />}
                      sx={{ mr: 1 }}
                      color="primary"
                      variant="outlined"
                    />
                  </Tooltip>
                ))}
              </Box>
            </Box>
            
            {/* Output Panel Selector */}
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1" gutterBottom>
                Output Panel
              </Typography>
              <Select
                value={selectedOutputPanel}
                onChange={(e) => setSelectedOutputPanel(e.target.value)}
                fullWidth
                size="small"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                {panels.map((panel, index) => (
                  <MenuItem key={panel.id} value={panel.id}>
                    Panel {index + 1}
                  </MenuItem>
                ))}
              </Select>
            </Box>
            
            {/* Control Buttons */}
            <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
              <Button
                variant="contained"
                color="primary"
                startIcon={<PlayIcon />}
                onClick={handleStartBrainstorm}
                disabled={isProcessing || sequence.length === 0}
                size="large"
              >
                Start Brainstorm
              </Button>
            </Box>
            
            {/* Results Display */}
            {results && (
              <Box sx={{ mt: 2 }}>
                <Typography variant="subtitle1" gutterBottom>
                  Results
                </Typography>
                <Paper variant="outlined" sx={{ p: 2 }}>
                  <Typography variant="body2" component="pre" sx={{ whiteSpace: 'pre-wrap' }}>
                    {JSON.stringify(results, null, 2)}
                  </Typography>
                </Paper>
              </Box>
            )}

            {/* Model Settings Dialog */}
            <Dialog 
              open={modelSettingsOpen} 
              onClose={() => setModelSettingsOpen(false)}
              maxWidth="sm"
              fullWidth
            >
              <DialogTitle>
                Model Settings for Panel {selectedPanelForSettings ? 
                  panels.findIndex(p => p.id === selectedPanelForSettings.panelId) + 1 : 
                  ''}
              </DialogTitle>
              <DialogContent>
                <FormControl fullWidth sx={{ mt: 2 }}>
                  <InputLabel>Model</InputLabel>
                  <Select
                    value={selectedPanelForSettings?.modelId || ''}
                    onChange={(e) => handleModelChange(e.target.value)}
                    label="Model"
                  >
                    {availableModels.map((model) => (
                      <MenuItem key={model.id} value={model.id}>
                        {model.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setModelSettingsOpen(false)}>Cancel</Button>
              </DialogActions>
            </Dialog>
          </Paper>
        </Collapse>
      </Box>
    </ClickAwayListener>
  );
};

export default React.memo(BrainstormControl);
