import React, { useState, useEffect, useMemo } from 'react';
import {
  Box,
  Typography,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Divider,
  Paper,
  Tooltip,
  Fab,
  TextField,
  Chip,
  Menu,
  MenuItem,
  InputAdornment,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Select,
  FormControl,
  InputLabel,
} from '@mui/material';
import {
  Close as CloseIcon,
  Save as SaveIcon,
  Delete as DeleteIcon,
  Edit as EditIcon,
  NoteAdd as NoteAddIcon,
  MenuBook as NotebookIcon,
  Search as SearchIcon,
  Label as LabelIcon,
  Share as ShareIcon,
  GetApp as ExportIcon,
  Upload as ImportIcon,
  Sort as SortIcon,
  Add as AddIcon,
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';
import ReactMarkdown from 'react-markdown';

const ResizeHandle = styled('div')(({ theme }) => ({
  position: 'absolute',
  left: -4,
  top: 0,
  bottom: 0,
  width: 8,
  cursor: 'ew-resize',
  '&:hover': {
    backgroundColor: theme.palette.action.hover,
  },
  '&::after': {
    content: '""',
    position: 'absolute',
    left: 3,
    top: 0,
    bottom: 0,
    width: 2,
    backgroundColor: theme.palette.divider,
  },
}));

const StyledDrawer = styled(Drawer)(({ theme }) => ({
  '& .MuiDrawer-paper': {
    minWidth: '300px',
    maxWidth: '90vw',
    width: localStorage.getItem('notebook-width') || '400px',
    backgroundColor: theme.palette.background.default,
    borderLeft: `1px solid ${theme.palette.divider}`,
    position: 'relative', // Required for absolute positioning of resize handle
  },
}));

const NotebookHeader = styled(Box)(({ theme }) => ({
  padding: theme.spacing(2),
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  borderBottom: `1px solid ${theme.palette.divider}`,
  backgroundColor: theme.palette.background.paper,
}));

const NotebookContent = styled(Box)(({ theme }) => ({
  height: 'calc(100vh - 64px)', // Adjust based on header height
  overflow: 'auto',
  padding: theme.spacing(2),
}));

const NotebookEntry = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  marginBottom: theme.spacing(2),
  backgroundColor: theme.palette.background.paper,
  '&:hover': {
    boxShadow: theme.shadows[4],
  },
}));

const FloatingButton = styled(Fab)(({ theme }) => ({
  position: 'fixed',
  right: theme.spacing(3),
  bottom: theme.spacing(3),
  zIndex: theme.zIndex.drawer - 1,
}));

const EmptyState = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  height: '100%',
  padding: theme.spacing(3),
  color: theme.palette.text.secondary,
  '& svg': {
    fontSize: 48,
    marginBottom: theme.spacing(2),
    opacity: 0.5,
  },
}));

function Notebook() {
  const [isOpen, setIsOpen] = useState(false);
  const [notebooks, setNotebooks] = useState(() => {
    const saved = localStorage.getItem('notebooks');
    return saved ? JSON.parse(saved) : [{ id: 'default', name: 'Default Notebook', entries: [] }];
  });
  const [currentNotebookId, setCurrentNotebookId] = useState(() => {
    const saved = localStorage.getItem('current-notebook');
    return saved || 'default';
  });
  const [notebookDialogOpen, setNotebookDialogOpen] = useState(false);
  const [notebookDialogMode, setNotebookDialogMode] = useState('add'); // 'add' or 'edit'
  const [notebookDialogData, setNotebookDialogData] = useState({ name: '' });
  const [entries, setEntries] = useState(() => {
    return notebooks.find(n => n.id === currentNotebookId)?.entries || [];
  });
  const [editMode, setEditMode] = useState(false);
  const [selectedEntry, setSelectedEntry] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortMenuAnchor, setSortMenuAnchor] = useState(null);
  const [sortBy, setSortBy] = useState('timestamp');
  const [sortDirection, setSortDirection] = useState('desc');
  const [categories, setCategories] = useState(() => {
    const saved = localStorage.getItem('notebook-categories');
    return saved ? JSON.parse(saved) : ['General', 'Important', 'Code', 'Ideas'];
  });
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [editingEntry, setEditingEntry] = useState(null);

  useEffect(() => {
    localStorage.setItem('notebooks', JSON.stringify(notebooks));
  }, [notebooks]);

  useEffect(() => {
    localStorage.setItem('current-notebook', currentNotebookId);
  }, [currentNotebookId]);

  useEffect(() => {
    const handleAddToNotebook = (event) => {
      const { detail } = event;
      if (detail) {
        setEntries(prev => [...prev, {
          id: Date.now(),
          ...detail,
          category: 'General',
          tags: [],
          timestamp: new Date().toISOString()
        }]);
        setIsOpen(true);
      }
    };

    window.addEventListener('addToNotebook', handleAddToNotebook);
    return () => window.removeEventListener('addToNotebook', handleAddToNotebook);
  }, []);

  useEffect(() => {
    setNotebooks(prev => prev.map(notebook =>
      notebook.id === currentNotebookId
        ? { ...notebook, entries }
        : notebook
    ));
  }, [entries, currentNotebookId]);

  const currentNotebook = useMemo(() => 
    notebooks.find(n => n.id === currentNotebookId) || notebooks[0],
    [notebooks, currentNotebookId]
  );

  const handleDelete = (entryId) => {
    setEntries(prev => prev.filter(entry => entry.id !== entryId));
  };

  const handleEdit = (entry) => {
    setEditingEntry({
      ...entry,
      newContent: entry.content,
      newCategory: entry.category || 'General',
      newTags: entry.tags || []
    });
    setEditDialogOpen(true);
  };

  const handleSaveEdit = () => {
    if (editingEntry) {
      setEntries(prev => prev.map(entry => 
        entry.id === editingEntry.id
          ? {
              ...entry,
              content: editingEntry.newContent,
              category: editingEntry.newCategory,
              tags: editingEntry.newTags,
              lastEdited: new Date().toISOString()
            }
          : entry
      ));
    }
    setEditDialogOpen(false);
    setEditingEntry(null);
  };

  const handleAddCategory = (newCategory) => {
    if (newCategory && !categories.includes(newCategory)) {
      setCategories(prev => [...prev, newCategory]);
    }
  };

  const handleAddTag = (entryId, newTag) => {
    setEntries(prev => prev.map(entry =>
      entry.id === entryId
        ? { ...entry, tags: [...(entry.tags || []), newTag] }
        : entry
    ));
  };

  const handleExport = () => {
    const exportData = {
      entries,
      categories,
      version: '1.0'
    };
    const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `notebook-export-${new Date().toISOString()}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleImport = (event) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const importedData = JSON.parse(e.target.result);
          if (importedData.version && importedData.entries) {
            setEntries(prev => [...prev, ...importedData.entries]);
            if (importedData.categories) {
              setCategories(prev => [...new Set([...prev, ...importedData.categories])]);
            }
          }
        } catch (error) {
          console.error('Error importing notebook:', error);
        }
      };
      reader.readAsText(file);
    }
  };

  const handleAddNotebook = () => {
    setNotebookDialogData({ name: '' });
    setNotebookDialogMode('add');
    setNotebookDialogOpen(true);
  };

  const handleEditNotebook = (notebook) => {
    setNotebookDialogData({ id: notebook.id, name: notebook.name });
    setNotebookDialogMode('edit');
    setNotebookDialogOpen(true);
  };

  const handleDeleteNotebook = (notebookId) => {
    if (notebooks.length === 1) {
      // Don't allow deleting the last notebook
      return;
    }

    setNotebooks(prev => {
      const filtered = prev.filter(n => n.id !== notebookId);
      if (currentNotebookId === notebookId) {
        // If we're deleting the current notebook, switch to another one
        setCurrentNotebookId(filtered[0].id);
      }
      return filtered;
    });
  };

  const handleNotebookDialogSave = () => {
    if (!notebookDialogData.name.trim()) return;

    if (notebookDialogMode === 'add') {
      const newNotebook = {
        id: Date.now().toString(),
        name: notebookDialogData.name.trim(),
        entries: []
      };
      setNotebooks(prev => [...prev, newNotebook]);
      setCurrentNotebookId(newNotebook.id);
    } else {
      setNotebooks(prev => prev.map(notebook =>
        notebook.id === notebookDialogData.id
          ? { ...notebook, name: notebookDialogData.name.trim() }
          : notebook
      ));
    }
    setNotebookDialogOpen(false);
  };

  const filteredAndSortedEntries = entries
    .filter(entry => {
      const matchesSearch = entry.content.toLowerCase().includes(searchQuery.toLowerCase()) ||
                          entry.metadata?.modelName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                          (entry.tags || []).some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()));
      const matchesCategory = selectedCategory === 'All' || entry.category === selectedCategory;
      return matchesSearch && matchesCategory;
    })
    .sort((a, b) => {
      const aValue = sortBy === 'timestamp' ? a.timestamp : a.content;
      const bValue = sortBy === 'timestamp' ? b.timestamp : b.content;
      return sortDirection === 'desc' ? 
        bValue.localeCompare(aValue) :
        aValue.localeCompare(bValue);
    });

  const handleResize = (e) => {
    const startX = e.clientX;
    const startWidth = e.currentTarget.parentElement.offsetWidth;
    
    const doDrag = (e) => {
      const newWidth = Math.max(300, Math.min(window.innerWidth * 0.9, startWidth - (e.clientX - startX)));
      e.currentTarget.parentElement.style.width = `${newWidth}px`;
    };

    const stopDrag = (e) => {
      document.removeEventListener('mousemove', doDrag);
      document.removeEventListener('mouseup', stopDrag);
      
      // Save the final width to localStorage
      const finalWidth = e.currentTarget.parentElement.offsetWidth;
      localStorage.setItem('notebook-width', `${finalWidth}px`);
    };

    document.addEventListener('mousemove', doDrag);
    document.addEventListener('mouseup', stopDrag);
  };

  return (
    <>
      <FloatingButton
        color="primary"
        onClick={() => setIsOpen(true)}
        aria-label="open notebook"
      >
        <NotebookIcon />
      </FloatingButton>

      <StyledDrawer
        anchor="right"
        open={isOpen}
        onClose={() => setIsOpen(false)}
      >
        <ResizeHandle onMouseDown={handleResize} />
        <NotebookHeader>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Typography variant="h6">My Notebooks</Typography>
            <Tooltip title="Add New Notebook">
              <IconButton size="small" onClick={handleAddNotebook}>
                <AddIcon />
              </IconButton>
            </Tooltip>
          </Box>
          <Box sx={{ display: 'flex', gap: 1 }}>
            <FormControl size="small" sx={{ minWidth: 120 }}>
              <Select
                value={currentNotebookId}
                onChange={(e) => setCurrentNotebookId(e.target.value)}
                variant="outlined"
                sx={{ height: 32 }}
              >
                {notebooks.map(notebook => (
                  <MenuItem key={notebook.id} value={notebook.id}>
                    {notebook.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <Tooltip title="Edit Current Notebook">
              <IconButton 
                size="small" 
                onClick={() => handleEditNotebook(currentNotebook)}
              >
                <EditIcon />
              </IconButton>
            </Tooltip>
            <Tooltip title={notebooks.length === 1 ? "Cannot delete the last notebook" : "Delete Current Notebook"}>
              <span>
                <IconButton 
                  size="small" 
                  onClick={() => handleDeleteNotebook(currentNotebookId)}
                  disabled={notebooks.length === 1}
                >
                  <DeleteIcon />
                </IconButton>
              </span>
            </Tooltip>
            <Tooltip title="Export Notebook">
              <IconButton onClick={handleExport}>
                <ExportIcon />
              </IconButton>
            </Tooltip>
            <Tooltip title="Import Notebook">
              <IconButton component="label">
                <input
                  type="file"
                  hidden
                  accept=".json"
                  onChange={handleImport}
                />
                <ImportIcon />
              </IconButton>
            </Tooltip>
            <IconButton onClick={() => setIsOpen(false)}>
              <CloseIcon />
            </IconButton>
          </Box>
        </NotebookHeader>

        <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider' }}>
          <TextField
            fullWidth
            placeholder="Search entries..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
            size="small"
          />
          <Box sx={{ mt: 2, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
            <Chip
              label="All"
              onClick={() => setSelectedCategory('All')}
              color={selectedCategory === 'All' ? 'primary' : 'default'}
            />
            {categories.map(category => (
              <Chip
                key={category}
                label={category}
                onClick={() => setSelectedCategory(category)}
                color={selectedCategory === category ? 'primary' : 'default'}
              />
            ))}
          </Box>
        </Box>

        <NotebookContent>
          {filteredAndSortedEntries.length === 0 ? (
            <EmptyState>
              <NoteAddIcon />
              <Typography variant="body1">
                {searchQuery
                  ? 'No entries match your search'
                  : 'Your notebook is empty. Drag and drop messages here or use the "Add to Notebook" option in message menus.'}
              </Typography>
            </EmptyState>
          ) : (
            filteredAndSortedEntries.map((entry) => (
              <NotebookEntry key={entry.id} elevation={1}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Box>
                    <Typography variant="caption" color="textSecondary">
                      {new Date(entry.timestamp).toLocaleString()}
                    </Typography>
                    <Chip
                      size="small"
                      label={entry.category || 'General'}
                      sx={{ ml: 1 }}
                    />
                  </Box>
                  <Box>
                    <Tooltip title="Edit">
                      <IconButton size="small" onClick={() => handleEdit(entry)}>
                        <EditIcon fontSize="small" />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Delete">
                      <IconButton size="small" onClick={() => handleDelete(entry.id)}>
                        <DeleteIcon fontSize="small" />
                      </IconButton>
                    </Tooltip>
                  </Box>
                </Box>
                <ReactMarkdown>{entry.content}</ReactMarkdown>
                {entry.metadata && (
                  <Typography variant="caption" color="textSecondary" sx={{ mt: 1, display: 'block' }}>
                    From: {entry.metadata.modelName || 'Unknown Model'}
                  </Typography>
                )}
                {entry.tags && entry.tags.length > 0 && (
                  <Box sx={{ mt: 1, display: 'flex', gap: 0.5, flexWrap: 'wrap' }}>
                    {entry.tags.map(tag => (
                      <Chip
                        key={tag}
                        label={tag}
                        size="small"
                        variant="outlined"
                      />
                    ))}
                  </Box>
                )}
              </NotebookEntry>
            ))
          )}
        </NotebookContent>
      </StyledDrawer>

      <Dialog
        open={notebookDialogOpen}
        onClose={() => setNotebookDialogOpen(false)}
      >
        <DialogTitle>
          {notebookDialogMode === 'add' ? 'Add New Notebook' : 'Edit Notebook'}
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Notebook Name"
            fullWidth
            value={notebookDialogData.name}
            onChange={(e) => setNotebookDialogData(prev => ({ ...prev, name: e.target.value }))}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setNotebookDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleNotebookDialogSave} variant="contained">
            {notebookDialogMode === 'add' ? 'Add' : 'Save'}
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog
        open={editDialogOpen}
        onClose={() => setEditDialogOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Edit Entry</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            multiline
            rows={4}
            value={editingEntry?.newContent || ''}
            onChange={(e) => setEditingEntry(prev => ({ ...prev, newContent: e.target.value }))}
            sx={{ mt: 2 }}
          />
          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>Category</InputLabel>
            <Select
              value={editingEntry?.newCategory || 'General'}
              onChange={(e) => setEditingEntry(prev => ({ ...prev, newCategory: e.target.value }))}
              label="Category"
            >
              {categories.map(category => (
                <MenuItem key={category} value={category}>
                  {category}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle2" gutterBottom>
              Tags
            </Typography>
            <Box sx={{ display: 'flex', gap: 0.5, flexWrap: 'wrap' }}>
              {editingEntry?.newTags?.map(tag => (
                <Chip
                  key={tag}
                  label={tag}
                  onDelete={() => setEditingEntry(prev => ({
                    ...prev,
                    newTags: prev.newTags.filter(t => t !== tag)
                  }))}
                />
              ))}
              <TextField
                size="small"
                placeholder="Add tag..."
                onKeyPress={(e) => {
                  if (e.key === 'Enter' && e.target.value) {
                    setEditingEntry(prev => ({
                      ...prev,
                      newTags: [...(prev.newTags || []), e.target.value]
                    }));
                    e.target.value = '';
                  }
                }}
              />
            </Box>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleSaveEdit} variant="contained">
            Save Changes
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
}

export default Notebook;
