import React, { useState } from 'react';
import { Box, IconButton, Paper, Tooltip } from '@mui/material';
import { styled } from '@mui/material/styles';
import { Add as AddIcon, Close as CloseIcon } from '@mui/icons-material';

const PanelContainer = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'row',
  height: 'calc(100vh - 16px)',
  width: '100%',
  overflow: 'hidden',
  padding: theme.spacing(1),
}));

const Panel = styled(Paper)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  height: '100%',
  flex: 1,
  minWidth: 300,
  position: 'relative',
  overflow: 'hidden',
  backgroundColor: theme.palette.background.default,
}));

const PanelHeader = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(1, 2),
  borderBottom: `1px solid ${theme.palette.divider}`,
  backgroundColor: theme.palette.background.paper,
  userSelect: 'none',
}));

const PanelContent = styled(Box)({
  flex: 1,
  overflow: 'auto',
  position: 'relative',
  height: 'calc(100% - 48px)', // Account for header height
});

const AddPanelButton = styled(IconButton)(({ theme }) => ({
  position: 'fixed',
  bottom: theme.spacing(2),
  right: theme.spacing(2),
  zIndex: theme.zIndex.drawer + 1,
}));

function PanelLayout({ children }) {
  const [panels, setPanels] = useState([
    { id: 'panel-1' },
    { id: 'panel-2' }
  ]);

  const handleRemovePanel = (panelId) => {
    if (panels.length > 1) {
      setPanels(prev => prev.filter(p => p.id !== panelId));
    }
  };

  const handleAddNewPanel = () => {
    setPanels(prev => [...prev, { id: `panel-${prev.length + 1}` }]);
  };

  return (
    <>
      <PanelContainer>
        {panels.map((panel, index) => (
          <Panel key={panel.id} elevation={2}>
            <PanelHeader>
              Chat Window {index + 1}
              {panels.length > 1 && (
                <IconButton
                  size="small"
                  onClick={() => handleRemovePanel(panel.id)}
                  sx={{ ml: 'auto' }}
                >
                  <CloseIcon fontSize="small" />
                </IconButton>
              )}
            </PanelHeader>
            <PanelContent>
              {typeof children === 'function' ? children(panel.id) : children}
            </PanelContent>
          </Panel>
        ))}
      </PanelContainer>
      
      <Tooltip title="Add new chat window">
        <AddPanelButton
          color="primary"
          onClick={handleAddNewPanel}
          size="large"
        >
          <AddIcon />
        </AddPanelButton>
      </Tooltip>
    </>
  );
}

export default PanelLayout;
