import React, { useState, useEffect } from 'react';
import { Box, Container, Grid, IconButton, Typography, Paper } from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import ChatWindow from './components/ChatWindow';
import ServerStatus from './components/ServerStatus';
import axios from 'axios';

function App() {
  const [serverStatus, setServerStatus] = useState('checking');
  const [models, setModels] = useState([]);
  const [chats, setChats] = useState({
    left: [],
    right: []
  });
  const [loading, setLoading] = useState({
    left: false,
    right: false
  });

  const checkServer = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/models');
      setServerStatus(response.data.status);
      setModels(response.data.models);
    } catch (error) {
      setServerStatus('offline');
      setModels([]);
    }
  };

  useEffect(() => {
    checkServer();
    // Poll for server status every 30 seconds
    const interval = setInterval(checkServer, 30000);
    return () => clearInterval(interval);
  }, []);

  const handleSendMessage = async (message, model, chatId) => {
    const updatedChats = {
      ...chats,
      [chatId]: [...chats[chatId], { role: 'user', content: message }]
    };
    setChats(updatedChats);
    setLoading(prev => ({ ...prev, [chatId]: true }));

    try {
      const response = await axios.post('http://localhost:5000/api/chat', {
        chatId,
        messages: updatedChats[chatId],
        model
      });

      const aiResponse = response.data.choices[0].message;
      setChats(prev => ({
        ...prev,
        [chatId]: [...prev[chatId], aiResponse]
      }));
    } catch (error) {
      console.error('Error sending message:', error);
      // Add error message to chat
      setChats(prev => ({
        ...prev,
        [chatId]: [...prev[chatId], { 
          role: 'assistant', 
          content: '❌ Error: Failed to get response from AI. Please try again.' 
        }]
      }));
    } finally {
      setLoading(prev => ({ ...prev, [chatId]: false }));
    }
  };

  return (
    <Container maxWidth="xl" sx={{ height: '100vh', py: 2 }}>
      <Box sx={{ mb: 2, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Typography variant="h4" component="h1">
          Dual Chat Interface
        </Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <ServerStatus status={serverStatus} />
          <IconButton onClick={checkServer} color="primary">
            <RefreshIcon />
          </IconButton>
        </Box>
      </Box>

      <Grid container spacing={2} sx={{ height: 'calc(100% - 80px)' }}>
        <Grid item xs={6}>
          <Paper elevation={3} sx={{ height: '100%', overflow: 'hidden' }}>
            <ChatWindow
              messages={chats.left}
              onSendMessage={(msg, model) => handleSendMessage(msg, model, 'left')}
              title="Chat Window 1"
              models={models}
              isLoading={loading.left}
            />
          </Paper>
        </Grid>
        <Grid item xs={6}>
          <Paper elevation={3} sx={{ height: '100%', overflow: 'hidden' }}>
            <ChatWindow
              messages={chats.right}
              onSendMessage={(msg, model) => handleSendMessage(msg, model, 'right')}
              title="Chat Window 2"
              models={models}
              isLoading={loading.right}
            />
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
