import React, { useState, useEffect } from 'react';
import { Box, Container, Grid, IconButton, Typography, Paper } from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import ChatWindow from './components/ChatWindow';
import ServerStatus from './components/ServerStatus';
import axios from 'axios';

function App() {
  const [serverStatus, setServerStatus] = useState('checking');
  const [models, setModels] = useState([]);
  const [chats, setChats] = useState(() => {
    // Load chat histories from localStorage
    const savedChats = localStorage.getItem('chatHistories');
    return savedChats ? JSON.parse(savedChats) : {
      left: [],
      right: []
    };
  });
  const [loading, setLoading] = useState({
    left: false,
    right: false
  });
  const [activeRequests, setActiveRequests] = useState({
    left: null,
    right: null
  });
  const [streamingResponses, setStreamingResponses] = useState({
    left: '',
    right: ''
  });

  // Save chats to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('chatHistories', JSON.stringify(chats));
  }, [chats]);

  const checkServer = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/models');
      setServerStatus(response.data.status);
      setModels(response.data.models);
    } catch (error) {
      setServerStatus('offline');
      setModels([]);
    }
  };

  useEffect(() => {
    checkServer();
    // Poll for server status every 30 seconds
    const interval = setInterval(checkServer, 30000);
    return () => clearInterval(interval);
  }, []);

  const handleStopResponse = (chatId) => {
    if (activeRequests[chatId]) {
      activeRequests[chatId].abort();
      setActiveRequests(prev => ({ ...prev, [chatId]: null }));
      setLoading(prev => ({ ...prev, [chatId]: false }));
      
      // Save the partial response if any
      if (streamingResponses[chatId]) {
        setChats(prev => ({
          ...prev,
          [chatId]: [...prev[chatId], { 
            role: 'assistant', 
            content: streamingResponses[chatId]
          }]
        }));
        setStreamingResponses(prev => ({ ...prev, [chatId]: '' }));
      }
    }
  };

  const handleSendMessage = async (message, model, chatId) => {
    const updatedChats = {
      ...chats,
      [chatId]: [...chats[chatId], { role: 'user', content: message }]
    };
    setChats(updatedChats);
    setLoading(prev => ({ ...prev, [chatId]: true }));
    setStreamingResponses(prev => ({ ...prev, [chatId]: '' }));

    // Create AbortController for the fetch request
    const controller = new AbortController();
    setActiveRequests(prev => ({ ...prev, [chatId]: controller }));

    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          chatId,
          messages: updatedChats[chatId],
          model
        }),
        signal: controller.signal
      });

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let responseText = '';

      while (true) {
        const { value, done } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        const lines = chunk.split('\n');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            try {
              const data = JSON.parse(line.slice(5));
              if (data.chunk) {
                responseText += data.chunk;
                setStreamingResponses(prev => ({
                  ...prev,
                  [chatId]: responseText
                }));
              }
            } catch (e) {
              console.error('Error parsing SSE data:', e);
            }
          }
        }
      }

      // Save the complete response
      setChats(prev => ({
        ...prev,
        [chatId]: [...prev[chatId], { 
          role: 'assistant', 
          content: responseText 
        }]
      }));
    } catch (error) {
      if (error.name !== 'AbortError') {
        console.error('Error sending message:', error);
        setChats(prev => ({
          ...prev,
          [chatId]: [...prev[chatId], { 
            role: 'assistant', 
            content: '❌ Error: Failed to get response from AI. Please try again.' 
          }]
        }));
      }
    } finally {
      setLoading(prev => ({ ...prev, [chatId]: false }));
      setActiveRequests(prev => ({ ...prev, [chatId]: null }));
      setStreamingResponses(prev => ({ ...prev, [chatId]: '' }));
    }
  };

  const clearChatHistory = (chatId) => {
    setChats(prev => ({
      ...prev,
      [chatId]: []
    }));
  };

  return (
    <Container maxWidth="xl" sx={{ height: '100vh', py: 2 }}>
      <Box sx={{ mb: 2, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Typography variant="h4" component="h1">
          Dual Chat Interface
        </Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <ServerStatus status={serverStatus} />
          <IconButton onClick={checkServer} color="primary">
            <RefreshIcon />
          </IconButton>
        </Box>
      </Box>

      <Grid container spacing={2} sx={{ height: 'calc(100% - 80px)' }}>
        <Grid item xs={6}>
          <Paper elevation={3} sx={{ height: '100%', overflow: 'hidden' }}>
            <ChatWindow
              messages={chats.left}
              streamingResponse={streamingResponses.left}
              onSendMessage={(msg, model) => handleSendMessage(msg, model, 'left')}
              onStopResponse={() => handleStopResponse('left')}
              title="Chat Window 1"
              models={models}
              isLoading={loading.left}
            />
          </Paper>
        </Grid>
        <Grid item xs={6}>
          <Paper elevation={3} sx={{ height: '100%', overflow: 'hidden' }}>
            <ChatWindow
              messages={chats.right}
              streamingResponse={streamingResponses.right}
              onSendMessage={(msg, model) => handleSendMessage(msg, model, 'right')}
              onStopResponse={() => handleStopResponse('right')}
              title="Chat Window 2"
              models={models}
              isLoading={loading.right}
            />
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
