import React, { useState, useEffect } from 'react';
import {
  Box,
  CssBaseline,
  CircularProgress,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  IconButton,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import {
  VideoCall as VideoIcon,
  OpenInFull as ExpandIcon,
  MoreVert as MoreIcon,
  Settings as SettingsIcon,
  Refresh as RefreshIcon,
} from '@mui/icons-material';
import ChatWindow from './components/ChatWindow';

const AppContainer = styled(Box)(({ theme }) => ({
  display: 'flex',
  height: '100vh',
  backgroundColor: theme.palette.primary.dark,
  padding: theme.spacing(2),
}));

const ChatContainer = styled(Box)(({ theme }) => ({
  display: 'flex',
  gap: theme.spacing(2),
  width: '100%',
  maxWidth: 1600,
  margin: '0 auto',
  height: '100%',
}));

const LoadingContainer = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  gap: theme.spacing(2),
  width: '100%',
  color: theme.palette.common.white,
}));

const RetryButton = styled(Button)(({ theme }) => ({
  marginTop: theme.spacing(2),
  backgroundColor: theme.palette.background.paper,
  color: theme.palette.primary.main,
  '&:hover': {
    backgroundColor: theme.palette.grey[100],
  },
}));

function App() {
  const [models, setModels] = useState([]);
  const [streamingResponses, setStreamingResponses] = useState({ left: '', right: '' });
  const [thinking, setThinking] = useState({ left: false, right: false });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [retryCount, setRetryCount] = useState(0);
  const [serverConfig, setServerConfig] = useState({
    address: localStorage.getItem('lmStudioAddress') || '192.168.56.1',
    port: localStorage.getItem('lmStudioPort') || '1234',
  });
  const [configOpen, setConfigOpen] = useState(false);

  const serverUrl = `http://${serverConfig.address}:${serverConfig.port}`;

  const handleConfigSave = () => {
    localStorage.setItem('lmStudioAddress', serverConfig.address);
    localStorage.setItem('lmStudioPort', serverConfig.port);
    setConfigOpen(false);
    setRetryCount(0); // Reset retry count to trigger immediate refresh
    setLoading(true); // Restart connection attempt
  };

  const fetchModels = async (retryAttempt = 0) => {
    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000);

      const response = await fetch(`${serverUrl}/v1/models`, {
        signal: controller.signal,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`Server returned ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      const modelList = data.models || [];
      if (!modelList.length) {
        throw new Error('No models available in LM Studio. Please load a model first.');
      }

      setModels(modelList.map(model => ({
        id: model,
        name: model.split('/').pop()
      })));
      setError(null);
      setRetryCount(0);
    } catch (error) {
      console.error('Error fetching models:', error);
      let errorMessage = error.message;
      if (error.name === 'AbortError') {
        errorMessage = 'Connection timed out. Is LM Studio running?';
      } else if (error.message === 'Failed to fetch') {
        errorMessage = `Unable to connect to LM Studio at ${serverUrl}.\nPlease check that:\n1. LM Studio is running\n2. Local Server is started in LM Studio\n3. The server address is correct`;
      }
      setError(errorMessage);

      // Implement exponential backoff for retries
      const maxRetries = 3;
      const backoffDelay = Math.min(1000 * Math.pow(2, retryAttempt), 10000);
      
      if (retryAttempt < maxRetries) {
        console.log(`Retrying in ${backoffDelay/1000} seconds... (Attempt ${retryAttempt + 1}/${maxRetries})`);
        setTimeout(() => {
          setRetryCount(retryAttempt + 1);
        }, backoffDelay);
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const initializeApp = async () => {
      await fetchModels(retryCount);
      // Only set up polling if we have a successful connection
      if (!error) {
        const interval = setInterval(() => fetchModels(0), 30000);
        return () => clearInterval(interval);
      }
    };

    initializeApp();
  }, [retryCount, serverUrl]);

  const handleRetry = () => {
    setLoading(true);
    setRetryCount(0);
  };

  if (loading) {
    return (
      <AppContainer>
        <CssBaseline />
        <LoadingContainer>
          <CircularProgress color="inherit" size={40} />
          <Typography variant="h6">
            Connecting to LM Studio...
          </Typography>
          <Typography variant="body2" sx={{ opacity: 0.7, textAlign: 'center' }}>
            Attempting to connect to {serverUrl}
          </Typography>
          <Typography variant="body2" sx={{ opacity: 0.7, textAlign: 'center' }}>
            Make sure LM Studio is running and the Local Server is started
          </Typography>
        </LoadingContainer>
      </AppContainer>
    );
  }

  if (error) {
    return (
      <AppContainer>
        <CssBaseline />
        <LoadingContainer>
          <Typography variant="h6" color="error">
            Unable to connect to LM Studio
          </Typography>
          <Typography variant="body1" color="error.light" sx={{ whiteSpace: 'pre-line', textAlign: 'center' }}>
            {error}
          </Typography>
          <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
            <RetryButton
              variant="contained"
              startIcon={<RefreshIcon />}
              onClick={handleRetry}
            >
              Retry Connection
            </RetryButton>
            <RetryButton
              variant="contained"
              startIcon={<SettingsIcon />}
              onClick={() => setConfigOpen(true)}
            >
              Configure Server
            </RetryButton>
          </Box>
        </LoadingContainer>

        <Dialog open={configOpen} onClose={() => setConfigOpen(false)}>
          <DialogTitle>Configure LM Studio Server</DialogTitle>
          <DialogContent>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, pt: 1 }}>
              <TextField
                label="Server Address"
                value={serverConfig.address}
                onChange={(e) => setServerConfig(prev => ({ ...prev, address: e.target.value }))}
                helperText="Example: localhost or 192.168.56.1"
                fullWidth
              />
              <TextField
                label="Port"
                value={serverConfig.port}
                onChange={(e) => setServerConfig(prev => ({ ...prev, port: e.target.value }))}
                helperText="Default: 1234"
                fullWidth
              />
            </Box>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setConfigOpen(false)}>Cancel</Button>
            <Button onClick={handleConfigSave} variant="contained">
              Save & Reconnect
            </Button>
          </DialogActions>
        </Dialog>
      </AppContainer>
    );
  }

  return (
    <AppContainer>
      <CssBaseline />
      <Box sx={{ position: 'absolute', top: 16, right: 16 }}>
        <IconButton 
          onClick={() => setConfigOpen(true)}
          sx={{ color: 'white' }}
        >
          <SettingsIcon />
        </IconButton>
      </Box>
      <ChatContainer>
        <ChatWindow
          position="left"
          models={models}
          streamingResponse={streamingResponses.left}
          isThinking={thinking.left}
          setStreamingResponses={setStreamingResponses}
          setThinking={setThinking}
          serverUrl={serverUrl}
        />
        <ChatWindow
          position="right"
          models={models}
          streamingResponse={streamingResponses.right}
          isThinking={thinking.right}
          setStreamingResponses={setStreamingResponses}
          setThinking={setThinking}
          serverUrl={serverUrl}
        />
      </ChatContainer>
    </AppContainer>
  );
}

export default App;
