import React, { useState, useRef, useEffect } from 'react';
import {
  Box,
  TextField,
  IconButton,
  Typography,
  Paper,
  List,
  ListItem,
  Avatar,
  CircularProgress,
  Tooltip
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import StopIcon from '@mui/icons-material/Stop';
import ReactMarkdown from 'react-markdown';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PersonIcon from '@mui/icons-material/Person';
import ModelSelector from './ModelSelector';

const ChatWindow = ({ 
  messages, 
  streamingResponse, 
  onSendMessage, 
  title, 
  models, 
  isLoading, 
  onStopResponse 
}) => {
  const [input, setInput] = useState('');
  const [selectedModel, setSelectedModel] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, streamingResponse]);

  useEffect(() => {
    if (models.length > 0 && !selectedModel) {
      setSelectedModel(models[0].id);
    }
  }, [models, selectedModel]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim() && selectedModel) {
      onSendMessage(input.trim(), selectedModel);
      setInput('');
    }
  };

  return (
    <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Box
        sx={{
          p: 2,
          borderBottom: '1px solid',
          borderColor: 'divider',
          backgroundColor: 'primary.main',
          color: 'white',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}
      >
        <Typography variant="h6">{title}</Typography>
        <ModelSelector
          models={models}
          selectedModel={selectedModel}
          onModelChange={setSelectedModel}
          disabled={isLoading}
        />
      </Box>

      <List
        sx={{
          flex: 1,
          overflow: 'auto',
          bgcolor: 'background.default',
          p: 2
        }}
      >
        {messages.map((message, index) => (
          <ListItem
            key={index}
            sx={{
              display: 'flex',
              gap: 2,
              backgroundColor: message.role === 'assistant' ? 'grey.50' : 'transparent',
              borderRadius: 1,
              mb: 2,
              p: 2
            }}
          >
            <Avatar
              sx={{
                bgcolor: message.role === 'assistant' ? 'primary.main' : 'secondary.main'
              }}
            >
              {message.role === 'assistant' ? <SmartToyIcon /> : <PersonIcon />}
            </Avatar>
            <Box sx={{ flex: 1 }}>
              <Typography
                variant="body1"
                component="div"
                sx={{
                  '& p': { m: 0 },
                  '& pre': {
                    bgcolor: 'grey.100',
                    p: 1,
                    borderRadius: 1,
                    overflow: 'auto'
                  }
                }}
              >
                <ReactMarkdown>{message.content}</ReactMarkdown>
              </Typography>
            </Box>
          </ListItem>
        ))}
        
        {/* Streaming response */}
        {streamingResponse && (
          <ListItem
            sx={{
              display: 'flex',
              gap: 2,
              backgroundColor: 'grey.50',
              borderRadius: 1,
              mb: 2,
              p: 2
            }}
          >
            <Avatar sx={{ bgcolor: 'primary.main' }}>
              <SmartToyIcon />
            </Avatar>
            <Box sx={{ flex: 1 }}>
              <Typography
                variant="body1"
                component="div"
                sx={{
                  '& p': { m: 0 },
                  '& pre': {
                    bgcolor: 'grey.100',
                    p: 1,
                    borderRadius: 1,
                    overflow: 'auto'
                  }
                }}
              >
                <ReactMarkdown>{streamingResponse}</ReactMarkdown>
              </Typography>
            </Box>
          </ListItem>
        )}

        {isLoading && !streamingResponse && (
          <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 2, my: 2 }}>
            <CircularProgress size={20} />
            <Tooltip title="Stop response">
              <IconButton 
                size="small" 
                onClick={onStopResponse}
                sx={{ 
                  opacity: 0.7,
                  '&:hover': { opacity: 1 }
                }}
              >
                <StopIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          </Box>
        )}
        <div ref={messagesEndRef} />
      </List>

      <Paper
        component="form"
        onSubmit={handleSubmit}
        sx={{
          p: 2,
          borderTop: '1px solid',
          borderColor: 'divider',
          display: 'flex',
          gap: 1
        }}
      >
        <TextField
          fullWidth
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          variant="outlined"
          size="small"
          multiline
          maxRows={4}
          disabled={isLoading || !selectedModel}
          onKeyPress={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              handleSubmit(e);
            }
          }}
        />
        <IconButton 
          type="submit" 
          color="primary" 
          disabled={!input.trim() || isLoading || !selectedModel}
        >
          <SendIcon />
        </IconButton>
      </Paper>
    </Box>
  );
};

export default ChatWindow;
