import React, { useState, useRef, useEffect } from 'react';
import {
  Box,
  Paper,
  TextField,
  IconButton,
  Typography,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  ListItemAvatar,
  Avatar,
  Menu,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  CircularProgress,
  Tooltip,
  InputAdornment,
} from '@mui/material';
import {
  Send as SendIcon,
  MoreVert as MoreVertIcon,
  History as HistoryIcon,
  Delete as DeleteIcon,
  Search as SearchIcon,
  Stop as StopIcon,
  SmartToy as BotIcon,
  Person as PersonIcon,
} from '@mui/icons-material';
import ReactMarkdown from 'react-markdown';
import { styled } from '@mui/material/styles';

const StyledPaper = styled(Paper)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  height: '100%',
  flex: 1,
  overflow: 'hidden',
  backgroundColor: theme.palette.background.default,
}));

const MessageList = styled(List)(({ theme }) => ({
  flexGrow: 1,
  overflow: 'auto',
  padding: theme.spacing(2),
  '&::-webkit-scrollbar': {
    width: '8px',
  },
  '&::-webkit-scrollbar-track': {
    background: theme.palette.background.default,
  },
  '&::-webkit-scrollbar-thumb': {
    background: theme.palette.grey[300],
    borderRadius: '4px',
    '&:hover': {
      background: theme.palette.grey[400],
    },
  },
}));

const MessageItem = styled(ListItem)(({ theme, align }) => ({
  flexDirection: 'column',
  alignItems: align === 'right' ? 'flex-end' : 'flex-start',
  padding: theme.spacing(1),
}));

const MessageContent = styled(Box)(({ theme, align }) => ({
  maxWidth: '70%',
  padding: theme.spacing(1.5),
  borderRadius: theme.spacing(2),
  backgroundColor: align === 'right' ? theme.palette.primary.main : theme.palette.grey[100],
  color: align === 'right' ? theme.palette.primary.contrastText : theme.palette.text.primary,
}));

const ChatHeader = styled(Box)(({ theme }) => ({
  padding: theme.spacing(2),
  borderBottom: `1px solid ${theme.palette.divider}`,
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  backgroundColor: theme.palette.background.paper,
}));

const ChatFooter = styled(Box)(({ theme }) => ({
  padding: theme.spacing(2),
  borderTop: `1px solid ${theme.palette.divider}`,
  backgroundColor: theme.palette.background.paper,
}));

function ChatWindow({
  position,
  models,
  streamingResponse,
  isThinking,
  setStreamingResponses,
  setThinking,
}) {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [selectedModel, setSelectedModel] = useState('');
  const [menuAnchor, setMenuAnchor] = useState(null);
  const [historyAnchor, setHistoryAnchor] = useState(null);
  const messagesEndRef = useRef(null);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    scrollToBottom();
  }, [messages, streamingResponse]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || !selectedModel) return;

    const newMessage = {
      content: inputValue,
      timestamp: new Date().toISOString(),
      role: 'user',
    };

    setMessages(prev => [...prev, newMessage]);
    setInputValue('');
    setThinking(prev => ({ ...prev, [position]: true }));

    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          message: inputValue,
          model: selectedModel,
        }),
      });

      const reader = response.body.getReader();
      let responseText = '';

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = new TextDecoder().decode(value);
        const data = JSON.parse(chunk.split('\n')[0]);

        if (data.status === 'thinking') {
          setThinking(prev => ({ ...prev, [position]: true }));
        } else if (data.status === 'streaming') {
          setThinking(prev => ({ ...prev, [position]: false }));
          responseText += data.chunk;
          setStreamingResponses(prev => ({
            ...prev,
            [position]: responseText,
          }));
        } else if (data.status === 'done') {
          setMessages(prev => [
            ...prev,
            {
              content: responseText,
              timestamp: new Date().toISOString(),
              role: 'assistant',
            },
          ]);
          setStreamingResponses(prev => ({ ...prev, [position]: '' }));
        }
      }
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [
        ...prev,
        {
          content: 'Failed to get response from AI. Please try again.',
          timestamp: new Date().toISOString(),
          role: 'error',
        },
      ]);
    } finally {
      setThinking(prev => ({ ...prev, [position]: false }));
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSendMessage();
    }
  };

  const handleClearHistory = () => {
    setMessages([]);
    setMenuAnchor(null);
    setHistoryAnchor(null);
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString();
  };

  const filteredMessages = messages.filter(msg =>
    msg.content.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <StyledPaper elevation={3}>
      <ChatHeader>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <FormControl sx={{ minWidth: 200 }}>
            <InputLabel>Select Model</InputLabel>
            <Select
              value={selectedModel}
              onChange={(e) => setSelectedModel(e.target.value)}
              label="Select Model"
            >
              {models.map((model) => (
                <MenuItem key={model.id} value={model.id}>
                  {model.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Box>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Tooltip title="Chat History">
            <IconButton onClick={(e) => setHistoryAnchor(e.currentTarget)}>
              <HistoryIcon />
            </IconButton>
          </Tooltip>
          <IconButton onClick={(e) => setMenuAnchor(e.currentTarget)}>
            <MoreVertIcon />
          </IconButton>
        </Box>
      </ChatHeader>

      <MessageList>
        {filteredMessages.map((msg, index) => (
          <MessageItem key={index} align={msg.role === 'user' ? 'right' : 'left'}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 0.5 }}>
              <ListItemAvatar>
                <Avatar>
                  {msg.role === 'user' ? <PersonIcon /> : <BotIcon />}
                </Avatar>
              </ListItemAvatar>
              <Typography variant="caption" color="textSecondary">
                {formatTimestamp(msg.timestamp)}
              </Typography>
            </Box>
            <MessageContent align={msg.role === 'user' ? 'right' : 'left'}>
              <ReactMarkdown>{msg.content}</ReactMarkdown>
            </MessageContent>
          </MessageItem>
        ))}
        {streamingResponse && (
          <MessageItem align="left">
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 0.5 }}>
              <ListItemAvatar>
                <Avatar>
                  <BotIcon />
                </Avatar>
              </ListItemAvatar>
            </Box>
            <MessageContent align="left">
              <ReactMarkdown>{streamingResponse}</ReactMarkdown>
            </MessageContent>
          </MessageItem>
        )}
        {isThinking && (
          <MessageItem align="left">
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 0.5 }}>
              <ListItemAvatar>
                <Avatar>
                  <BotIcon />
                </Avatar>
              </ListItemAvatar>
            </Box>
            <MessageContent align="left">
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <CircularProgress size={20} />
                <Typography>Thinking...</Typography>
              </Box>
            </MessageContent>
          </MessageItem>
        )}
        <div ref={messagesEndRef} />
      </MessageList>

      <ChatFooter>
        <TextField
          fullWidth
          multiline
          maxRows={4}
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          variant="outlined"
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton
                  onClick={handleSendMessage}
                  disabled={!inputValue.trim() || !selectedModel}
                >
                  <SendIcon />
                </IconButton>
              </InputAdornment>
            ),
          }}
        />
      </ChatFooter>

      <Menu
        anchorEl={menuAnchor}
        open={Boolean(menuAnchor)}
        onClose={() => setMenuAnchor(null)}
      >
        <MenuItem onClick={handleClearHistory}>
          <ListItemIcon>
            <DeleteIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText primary="Clear Chat" />
        </MenuItem>
      </Menu>

      <Menu
        anchorEl={historyAnchor}
        open={Boolean(historyAnchor)}
        onClose={() => setHistoryAnchor(null)}
      >
        <Box sx={{ p: 2, width: 300 }}>
          <TextField
            fullWidth
            size="small"
            placeholder="Search messages..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
          />
        </Box>
        {filteredMessages.map((msg, index) => (
          <MenuItem key={index} sx={{ whiteSpace: 'normal' }}>
            <ListItemText
              primary={msg.content}
              secondary={`${msg.role} - ${formatTimestamp(msg.timestamp)}`}
            />
          </MenuItem>
        ))}
        <MenuItem onClick={handleClearHistory}>
          <ListItemIcon>
            <DeleteIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText primary="Clear Chat History" />
        </MenuItem>
      </Menu>
    </StyledPaper>
  );
}

export default ChatWindow;
