import React, { useState, useRef, useEffect } from 'react';
import {
  Box,
  TextField,
  IconButton,
  Typography,
  Paper,
  List,
  ListItem,
  Avatar,
  CircularProgress,
  Tooltip,
  Menu,
  MenuItem
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import StopIcon from '@mui/icons-material/Stop';
import HistoryIcon from '@mui/icons-material/History';
import DeleteIcon from '@mui/icons-material/Delete';
import ReactMarkdown from 'react-markdown';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PersonIcon from '@mui/icons-material/Person';
import ModelSelector from './ModelSelector';

const ChatWindow = ({ 
  messages, 
  streamingResponse, 
  onSendMessage, 
  title, 
  models, 
  isLoading,
  isThinking, 
  onStopResponse,
  onClearHistory 
}) => {
  const [input, setInput] = useState('');
  const [selectedModel, setSelectedModel] = useState('');
  const messagesEndRef = useRef(null);
  const [historyAnchor, setHistoryAnchor] = useState(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, streamingResponse]);

  useEffect(() => {
    if (models.length > 0 && !selectedModel) {
      setSelectedModel(models[0].id);
    }
  }, [models, selectedModel]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim() && selectedModel) {
      onSendMessage(input.trim(), selectedModel);
      setInput('');
    }
  };

  const handleHistoryClick = (event) => {
    setHistoryAnchor(event.currentTarget);
  };

  const handleHistoryClose = () => {
    setHistoryAnchor(null);
  };

  const handleClearHistory = () => {
    if (onClearHistory) {
      onClearHistory();
    }
    handleHistoryClose();
  };

  const formatTimestamp = (message, index) => {
    if (message.timestamp) {
      const date = new Date(message.timestamp);
      return date.toLocaleString();
    }
    return `Message ${index + 1}`;
  };

  return (
    <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Box
        sx={{
          p: 2,
          borderBottom: '1px solid',
          borderColor: 'divider',
          backgroundColor: 'primary.main',
          color: 'white',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}
      >
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Typography variant="h6">{title}</Typography>
          <Tooltip title="Chat History">
            <IconButton 
              size="small" 
              onClick={handleHistoryClick}
              sx={{ color: 'white', opacity: 0.9, '&:hover': { opacity: 1 } }}
            >
              <HistoryIcon />
            </IconButton>
          </Tooltip>
        </Box>
        <ModelSelector
          models={models}
          selectedModel={selectedModel}
          onModelChange={setSelectedModel}
          disabled={isLoading}
        />
      </Box>

      <Menu
        anchorEl={historyAnchor}
        open={Boolean(historyAnchor)}
        onClose={handleHistoryClose}
        PaperProps={{
          sx: {
            maxHeight: 400,
            width: '300px'
          }
        }}
      >
        <MenuItem 
          onClick={handleClearHistory}
          sx={{ 
            color: 'error.main',
            borderBottom: '1px solid',
            borderColor: 'divider',
            display: 'flex',
            gap: 1
          }}
        >
          <DeleteIcon fontSize="small" />
          Clear Chat History
        </MenuItem>
        {messages.map((message, index) => (
          <MenuItem 
            key={index}
            sx={{ 
              whiteSpace: 'normal',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
              borderBottom: '1px solid',
              borderColor: 'divider',
              py: 1
            }}
          >
            <Typography variant="caption" color="text.secondary">
              {formatTimestamp(message, index)} - {message.role}
            </Typography>
            <Typography
              variant="body2"
              sx={{
                overflow: 'hidden',
                textOverflow: 'ellipsis',
                display: '-webkit-box',
                WebkitLineClamp: 2,
                WebkitBoxOrient: 'vertical',
              }}
            >
              {message.content}
            </Typography>
          </MenuItem>
        ))}
      </Menu>

      <List
        sx={{
          flex: 1,
          overflowY: 'auto',
          overflowX: 'hidden',
          bgcolor: 'background.default',
          p: 2,
          '&::-webkit-scrollbar': {
            width: '8px',
            backgroundColor: 'transparent',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: 'rgba(0, 0, 0, 0.1)',
            borderRadius: '4px',
            '&:hover': {
              backgroundColor: 'rgba(0, 0, 0, 0.2)',
            },
          },
          '&::-webkit-scrollbar-track': {
            backgroundColor: 'transparent',
          },
          msOverflowStyle: 'none',  // IE and Edge
          scrollbarWidth: 'thin',    // Firefox
          scrollbarColor: 'rgba(0, 0, 0, 0.1) transparent', // Firefox
        }}
      >
        <Box sx={{ minHeight: '100%', display: 'flex', flexDirection: 'column' }}>
          {messages.map((message, index) => (
            <ListItem
              key={index}
              sx={{
                display: 'flex',
                gap: 2,
                backgroundColor: message.role === 'assistant' ? 'grey.50' : 'transparent',
                borderRadius: 1,
                mb: 2,
                p: 2,
                flexDirection: 'column',
                alignItems: 'stretch',
                width: '100%',
                maxWidth: '100%'
              }}
            >
              <Box sx={{ display: 'flex', gap: 2, width: '100%' }}>
                <Avatar
                  sx={{
                    bgcolor: message.role === 'assistant' ? 'primary.main' : 'secondary.main',
                    flexShrink: 0
                  }}
                >
                  {message.role === 'assistant' ? <SmartToyIcon /> : <PersonIcon />}
                </Avatar>
                <Box sx={{ flex: 1, minWidth: 0 }}>
                  <Typography variant="caption" color="text.secondary">
                    {formatTimestamp(message, index)}
                  </Typography>
                  <Typography
                    variant="body1"
                    component="div"
                    sx={{
                      '& p': { m: 0 },
                      '& pre': {
                        bgcolor: 'grey.100',
                        p: 1,
                        borderRadius: 1,
                        overflow: 'auto',
                        maxWidth: '100%'
                      },
                      wordBreak: 'break-word',
                      overflowWrap: 'break-word'
                    }}
                  >
                    <ReactMarkdown>{message.content}</ReactMarkdown>
                  </Typography>
                </Box>
              </Box>
            </ListItem>
          ))}
          
          {streamingResponse && (
            <ListItem
              sx={{
                display: 'flex',
                gap: 2,
                backgroundColor: 'grey.50',
                borderRadius: 1,
                mb: 2,
                p: 2,
                width: '100%',
                maxWidth: '100%'
              }}
            >
              <Avatar sx={{ bgcolor: 'primary.main', flexShrink: 0 }}>
                <SmartToyIcon />
              </Avatar>
              <Box sx={{ flex: 1, minWidth: 0 }}>
                <Typography
                  variant="body1"
                  component="div"
                  sx={{
                    '& p': { m: 0 },
                    '& pre': {
                      bgcolor: 'grey.100',
                      p: 1,
                      borderRadius: 1,
                      overflow: 'auto',
                      maxWidth: '100%'
                    },
                    wordBreak: 'break-word',
                    overflowWrap: 'break-word'
                  }}
                >
                  <ReactMarkdown>{streamingResponse}</ReactMarkdown>
                </Typography>
              </Box>
            </ListItem>
          )}

          {isThinking && (
            <ListItem
              sx={{
                display: 'flex',
                gap: 2,
                backgroundColor: 'grey.50',
                borderRadius: 1,
                mb: 2,
                p: 2,
                width: '100%',
                maxWidth: '100%'
              }}
            >
              <Avatar sx={{ bgcolor: 'primary.main', flexShrink: 0 }}>
                <SmartToyIcon />
              </Avatar>
              <Box sx={{ flex: 1, display: 'flex', alignItems: 'center', gap: 2 }}>
                <CircularProgress size={16} />
                <Typography variant="body1" color="text.secondary">
                  Thinking...
                </Typography>
              </Box>
            </ListItem>
          )}

          {isLoading && !isThinking && !streamingResponse && (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 2, my: 2 }}>
              <CircularProgress size={20} />
              <Tooltip title="Stop response">
                <IconButton 
                  size="small" 
                  onClick={onStopResponse}
                  sx={{ 
                    opacity: 0.7,
                    '&:hover': { opacity: 1 }
                  }}
                >
                  <StopIcon fontSize="small" />
                </IconButton>
              </Tooltip>
            </Box>
          )}
          <div ref={messagesEndRef} />
        </Box>
      </List>

      <Paper
        component="form"
        onSubmit={handleSubmit}
        sx={{
          p: 2,
          borderTop: '1px solid',
          borderColor: 'divider',
          display: 'flex',
          gap: 1
        }}
      >
        <TextField
          fullWidth
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          variant="outlined"
          size="small"
          multiline
          maxRows={4}
          disabled={isLoading || !selectedModel}
          onKeyPress={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              handleSubmit(e);
            }
          }}
        />
        <IconButton 
          type="submit" 
          color="primary" 
          disabled={!input.trim() || isLoading || !selectedModel}
        >
          <SendIcon />
        </IconButton>
      </Paper>
    </Box>
  );
};

export default ChatWindow;
