function ChatWindow({
  position,
  models,
  streamingResponse,
  isThinking,
  setStreamingResponses,
  setThinking,
  serverUrl,
  otherPanelMessages,
  onBrainstormMessage,
  onMessageSubmit,
  onModelSelect,
}) {
  // Process streaming chunks
  const processStreamingChunk = useCallback((chunk) => {
    const lines = chunk.split('\n');
    let accumulatedContent = '';

    for (const line of lines) {
      if (!line.trim() || line.includes('[DONE]')) continue;

      try {
        const jsonStr = line.replace(/^data: /, '');
        const data = JSON.parse(jsonStr);

        if (data.choices && data.choices[0]?.delta?.content) {
          accumulatedContent += data.choices[0].delta.content;
        }
      } catch (e) {
        console.warn('Error parsing chunk:', e);
        continue;
      }
    }

    return accumulatedContent;
  }, []);

  const processStreamingResponse = useCallback(async (reader, responseText = '', maxIterations = 1000) => {
    let iterations = 0;
    let accumulatedContent = responseBuffer.current || '';

    try {
      while (iterations < maxIterations) {
        iterations++;
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = new TextDecoder().decode(value);
        const newContent = processStreamingChunk(chunk);
        accumulatedContent += newContent;
        responseText += newContent;

        setStreamingResponses(prev => ({
          ...prev,
          [position]: accumulatedContent
        }));
      }

      return responseText;
    } catch (error) {
      console.error('Error processing stream:', error);
      throw error;
    }
  }, [processStreamingChunk, position, setStreamingResponses]);

  // Update initial selectedModel state to use localStorage
  const [selectedModel, setSelectedModel] = useState(() => {
    // Don't automatically select a model from localStorage
    return '';
  });

  const [inputValue, setInputValue] = useState('');
  const [brainstormEnabled, setBrainstormEnabled] = useState(() => {
    const saved = localStorage.getItem(`brainstorm-enabled-${position}`);
    return saved ? JSON.parse(saved) : false;
  });
  const [brainstormIterations, setBrainstormIterations] = useState(() => {
    const saved = localStorage.getItem(`brainstorm-iterations-${position}`);
    return saved ? JSON.parse(saved) : 0; // 0 means no iterations set, -1 means infinite
  });
  const [brainstormMenuAnchor, setBrainstormMenuAnchor] = useState(null);
  const [isEditingName, setIsEditingName] = useState(false);
  const [editedName, setEditedName] = useState('');
  const [conversations, setConversations] = useState(() => {
    const saved = localStorage.getItem(`conversations-${position}`);
    return saved ? JSON.parse(saved) : [{
      id: Date.now(),
      name: 'New Conversation',
      timestamp: new Date().toISOString(),
      messages: []
    }];
  });
  const [currentConversationId, setCurrentConversationId] = useState(() => {
    return conversations[0]?.id;
  });

  const currentConversation = useMemo(() => {
    return conversations.find(c => c.id === currentConversationId) || conversations[0];
  }, [conversations, currentConversationId]);

  // Save conversations to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem(`conversations-${position}`, JSON.stringify(conversations));
  }, [conversations, position]);

  const updateConversationMessages = useCallback((newMessages) => {
    setConversations(prev => prev.map(conv => 
      conv.id === currentConversationId
        ? { ...conv, messages: newMessages }
        : conv
    ));
  }, [currentConversationId]);

  const [menuAnchor, setMenuAnchor] = useState(null);
  const [emojiAnchor, setEmojiAnchor] = useState(null);
  const [uploadError, setUploadError] = useState('');

  // Refs
  const fileInputRef = useRef(null);
  const messagesEndRef = useRef(null);
  const abortControllerRef = useRef(null);
  const responseBuffer = useRef('');
  const updateTimeoutRef = useRef(null);

  // Add health check state
  const [lastHealthCheck, setLastHealthCheck] = useState(() => {
    return parseInt(localStorage.getItem('lastHealthCheck') || '0');
  });

  // Add health check toggle state
  const [healthChecksEnabled, setHealthChecksEnabled] = useState(() => {
    return localStorage.getItem('healthChecksEnabled') !== 'false';
  });

  // Add loading state for model switching
  const [isModelSwitching, setIsModelSwitching] = useState(false);

  // Add model status tracking
  const [modelStatus, setModelStatus] = useState({});
  const lastNetworkCheck = useRef(0);
  const networkCheckInterval = 60000; // 1 minute in milliseconds

  // Cache models list with a longer interval
  const [cachedModels, setCachedModels] = useState([]);
  const modelListInterval = 60000; // 1 minute
  const lastModelListCheck = useRef(0);

  // Add state for scroll management
  const [userHasScrolled, setUserHasScrolled] = useState(false);
  const messageListRef = useRef(null);

  // Function to check if scrolled to bottom
  const isScrolledToBottom = useCallback(() => {
    if (!messageListRef.current) return true;
    const { scrollHeight, scrollTop, clientHeight } = messageListRef.current;
    // Consider "almost" at bottom (within 100px) as at bottom
    return scrollHeight - scrollTop - clientHeight < 100;
  }, []);

  // Handle scroll events
  const handleScroll = useCallback((e) => {
    // Only set userHasScrolled if the scroll was manual (wheel or drag)
    if (e.type === 'wheel' || e.type === 'touchmove') {
      const wasAtBottom = isScrolledToBottom();
      setUserHasScrolled(!wasAtBottom);
    }
  }, [isScrolledToBottom]);

  // Scroll to bottom function
  const scrollToBottom = useCallback(() => {
    if (messagesEndRef.current && (!userHasScrolled || isScrolledToBottom())) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [userHasScrolled, isScrolledToBottom]);

  // Update scroll effect
  useEffect(() => {
    if (!messageListRef.current) return;

    const messageList = messageListRef.current;
    messageList.addEventListener('wheel', handleScroll);
    messageList.addEventListener('touchmove', handleScroll);

    return () => {
      messageList.removeEventListener('wheel', handleScroll);
      messageList.removeEventListener('touchmove', handleScroll);
    };
  }, [handleScroll]);

  // Update auto-scroll effect
  useEffect(() => {
    const scrollTimer = setTimeout(() => {
      scrollToBottom();
    }, 100);

    return () => clearTimeout(scrollTimer);
  }, [currentConversation.messages, streamingResponse, scrollToBottom]);

  // Add model tracking state at the top of component
  const [loadedModels, setLoadedModels] = useState({});

  // Add model queue state at component level
  const [modelQueue, setModelQueue] = useState([]);
  const [isLoadingModel, setIsLoadingModel] = useState(false);

  // Update handleModelChange to preserve models
  const handleModelChange = useCallback(async (modelId) => {
    if (modelId === selectedModel) return;

    setIsModelSwitching(true);
    try {
      // Load the new model with options to preserve other models
      await fetch(`${serverUrl}/v1/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          model: modelId,
          messages: [{ role: 'system', content: 'test' }],
          stream: false,
          max_tokens: 1,
          options: {
            load_model_only_when_needed: false,
            unload_model_after_completion: false,
            no_auto_model_selection: true,
            skip_model_load_test: true,
            keep_loaded_models: true  // Add this option to prevent unloading
          }
        })
      });

      // Update state and localStorage
      setSelectedModel(modelId);
      localStorage.setItem(`selectedModel-${position}`, modelId);
      onModelSelect?.(position, modelId);

    } catch (error) {
      console.error('Error switching model:', error);
    } finally {
      setIsModelSwitching(false);
    }
  }, [position, onModelSelect, selectedModel, serverUrl]);

  // Add effect to maintain loaded models
  useEffect(() => {
    const reloadModels = async () => {
      if (isLoadingModel || modelQueue.length === 0) return;

      setIsLoadingModel(true);
      try {
        // Reload all models in the queue to ensure they stay loaded
        for (const modelId of modelQueue) {
          await fetch(`${serverUrl}/v1/chat/completions`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              model: modelId,
              messages: [{ role: 'system', content: 'test' }],
              stream: false,
              max_tokens: 1,
              options: {
                load_model_only_when_needed: false,
                unload_model_after_completion: false,
                no_auto_model_selection: true,
                skip_model_load_test: true
              }
            })
          });
        }
      } catch (error) {
        console.error('Error reloading models:', error);
      } finally {
        setIsLoadingModel(false);
      }
    };

    const interval = setInterval(reloadModels, 30000); // Reload models every 30 seconds
    return () => clearInterval(interval);
  }, [modelQueue, serverUrl, isLoadingModel]);

  // Update model selection effect to be more conservative
  useEffect(() => {
    // Remove auto-selection of model from localStorage
    if (!selectedModel && cachedModels.length > 0) {
      // Don't automatically set a model
      return;
    }
  }, [cachedModels, selectedModel, handleModelChange, position]);

  // Update models fetch to respect existing selection
  useEffect(() => {
    const fetchModels = async () => {
      const now = Date.now();
      if (now - lastModelListCheck.current < modelListInterval) {
        return; // Use cached models if checked recently
      }

      try {
        const response = await fetch(`${serverUrl}/v1/models`, {
          method: 'GET',
          headers: { 'Cache-Control': 'no-cache' }
        });

        if (response.ok) {
          const data = await response.json();
          const newModels = data.data || [];
          setCachedModels(newModels);
          lastModelListCheck.current = now;

          // If current selected model is not in the new list, clear it
          if (selectedModel && !newModels.some(m => m.id === selectedModel)) {
            setSelectedModel('');
            localStorage.removeItem(`selectedModel-${position}`);
          }
        }
      } catch (error) {
        console.warn('Failed to fetch models:', error);
      }
    };

    fetchModels();
  }, [serverUrl, selectedModel, position]);

  // Update checkServerHealth to respect the toggle
  const checkServerHealth = useCallback(async () => {
    // If health checks are disabled, always return true
    if (!healthChecksEnabled) {
      return true;
    }

    const now = Date.now();
    if (now - lastNetworkCheck.current < networkCheckInterval) {
      return true; // Return true if we checked recently
    }

    try {
      const response = await fetch(`${serverUrl}/health`, {
        method: 'HEAD',
        signal: AbortSignal.timeout(5000)
      });

      if (response.ok) {
        lastNetworkCheck.current = now;
        return true;
      }
      return false;
    } catch (error) {
      console.warn('Health check failed:', error);
      return false;
    }
  }, [serverUrl, healthChecksEnabled]);

  // Add effect to save health check preference
  useEffect(() => {
    localStorage.setItem('healthChecksEnabled', healthChecksEnabled);
  }, [healthChecksEnabled]);

  // Move cleanupConversation before handleConversationChange
  const cleanupConversation = useCallback((conversationId) => {
    // Abort any ongoing requests
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
      abortControllerRef.current = null;
    }

    // Clear streaming responses
    setStreamingResponses(prev => ({
      ...prev,
      [position]: ''
    }));

    // Clear thinking state
    setThinking(prev => ({
      ...prev,
      [position]: false
    }));

    // Clear response buffer
    responseBuffer.current = '';

    // Clear any pending timeouts
    if (updateTimeoutRef.current) {
      clearTimeout(updateTimeoutRef.current);
      updateTimeoutRef.current = null;
    }
  }, [position, setStreamingResponses, setThinking]);

  // Handle conversation switch
  const handleConversationChange = useCallback((newId) => {
    if (!conversations.some(conv => conv.id === newId)) {
      console.warn('Invalid conversation ID, selecting first available conversation');
      newId = conversations[0]?.id || Date.now().toString();
    }

    // Clean up the current conversation before switching
    cleanupConversation(currentConversationId);
    setCurrentConversationId(newId);
  }, [conversations, currentConversationId, cleanupConversation]);

  // Optimize streaming updates
  const updateStreamingResponse = useCallback((newContent) => {
    responseBuffer.current = newContent;

    if (updateTimeoutRef.current) {
      clearTimeout(updateTimeoutRef.current);
    }

    updateTimeoutRef.current = setTimeout(() => {
      setStreamingResponses(prev => ({
        ...prev,
        [position]: responseBuffer.current
      }));
    }, 1500); // 1.5 second debounce
  }, [position, setStreamingResponses]);

  // Update handleSendMessage to preserve models
  const handleSendMessage = useCallback(async () => {
    if (!inputValue.trim() || !selectedModel || isModelSwitching) return;

    const message = inputValue.trim();
    setInputValue('');

    // Add user message to conversation
    const userMessage = {
      content: message,
      timestamp: new Date().toISOString(),
      role: 'user',
    };

    updateConversationMessages([...currentConversation.messages, userMessage]);

    try {
      setThinking(prev => ({ ...prev, [position]: true }));
      setStreamingResponses(prev => ({ ...prev, [position]: '' }));

      const response = await fetch(`${serverUrl}/v1/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          model: selectedModel,
          messages: currentConversation.messages.map(msg => ({
            role: msg.role,
            content: msg.content
          })),
          stream: true,
          options: {
            load_model_only_when_needed: false,
            unload_model_after_completion: false,
            no_auto_model_selection: true,
            skip_model_load_test: true,
            keep_loaded_models: true  // Add this option to prevent unloading
          }
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const reader = response.body.getReader();
      const result = await processStreamingResponse(reader);

    } catch (error) {
      console.error('Error sending message:', error);
      updateConversationMessages([
        ...currentConversation.messages,
        {
          content: `Error: ${error.message}`,
          timestamp: new Date().toISOString(),
          role: 'error',
        }
      ]);
    } finally {
      setThinking(prev => ({ ...prev, [position]: false }));
    }
  }, [
    inputValue,
    selectedModel,
    isModelSwitching,
    currentConversation.messages,
    updateConversationMessages,
    setStreamingResponses,
    position,
    serverUrl,
    processStreamingResponse,
    setThinking
  ]);

  // Update the brainstorm effect to handle message passing correctly
  useEffect(() => {
    if (!brainstormEnabled || !selectedModel) {
      console.log(`Panel ${position}: Brainstorm skipped - enabled: ${brainstormEnabled}, model: ${selectedModel}`);
      return;
    }

    // Get the latest message from the other panel
    const otherPanelMessage = otherPanelMessages?.[otherPanelMessages.length - 1];
    if (!otherPanelMessage) {
      console.log(`Panel ${position}: No messages from other panel`);
      return;
    }

    console.log(`Panel ${position}: Processing message:`, otherPanelMessage);

    // Only process assistant messages that haven't been processed
    if (otherPanelMessage.role !== 'assistant' || otherPanelMessage.processed === true) {
      console.log(`Panel ${position}: Message skipped - role: ${otherPanelMessage.role}, processed: ${otherPanelMessage.processed}`);
      return;
    }

    // Don't process if we're still thinking about the previous message
    if (isThinking) {
      console.log(`Panel ${position}: Waiting for previous message to complete`);
      return;
    }

    const currentIteration = otherPanelMessage.metadata?.iterationCount || 0;
    console.log(`Panel ${position}: Current iteration: ${currentIteration}, max: ${brainstormIterations}`);

    if (brainstormIterations !== -1 && currentIteration >= brainstormIterations) {
      console.log(`Panel ${position}: Stopping brainstorm after ${currentIteration} iterations`);
      return;
    }

    const nextIterationCount = currentIteration + 1;
    console.log(`Panel ${position}: Starting iteration ${nextIterationCount}`);

    // Process the message
    handleSendMessage(otherPanelMessage.content, {
      iterationCount: nextIterationCount,
      fromBrainstorm: true,
      fromPanel: position,
      timestamp: new Date().toISOString()
    });

  }, [
    brainstormEnabled,
    selectedModel,
    otherPanelMessages,
    position,
    isThinking,
    brainstormIterations,
    handleSendMessage
  ]);

  const handleClearChat = useCallback(async () => {
    try {
      // Clear conversation context by sending an empty completion request
      await fetch(`${serverUrl}/v1/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          model: selectedModel,
          messages: [], // Empty messages array to reset context
          stream: false,
          options: {
            load_model_only_when_needed: false,
            unload_model_after_completion: false,
            skip_embedding_model: true,
            skip_model_load_test: true,
            no_auto_model_selection: true,
            unload_other_models: false
          }
        })
      });

      // Clear local state
      updateConversationMessages([]); 
      setStreamingResponses(prev => ({
        ...prev,
        [position]: ''
      }));
      setThinking(prev => ({
        ...prev,
        [position]: false
      }));
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
        abortControllerRef.current = null;
      }
    } catch (error) {
      console.error('Error clearing chat:', error);
    }
  }, [position, setStreamingResponses, setThinking, updateConversationMessages, serverUrl, selectedModel]);

  const handleNewConversation = useCallback(() => {
    const newConversation = {
      id: Date.now(),
      name: 'New Conversation',
      timestamp: new Date().toISOString(),
      messages: []
    };
    setConversations(prev => [newConversation, ...prev]);
    setCurrentConversationId(newConversation.id);
  }, []);

  const handleMenuAction = useCallback(async (action) => {
    switch (action) {
      case 'new':
        handleNewConversation();
        break;
      case 'clear':
        handleClearChat();
        break;
      case 'delete':
        try {
          // Clean up the conversation before deleting
          cleanupConversation(currentConversationId);

          // Clear conversation context by sending an empty completion request
          await fetch(`${serverUrl}/v1/chat/completions`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              model: selectedModel,
              messages: [], // Empty messages array to reset context
              stream: false,
              options: {
                load_model_only_when_needed: false,
                unload_model_after_completion: false,
                skip_embedding_model: true,
                skip_model_load_test: true,
                no_auto_model_selection: true,
                unload_other_models: false
              }
            })
          });

          setConversations(prev => {
            const updatedConversations = prev.filter(conv => conv.id !== currentConversationId);
            if (updatedConversations.length === 0) {
              const newConversation = {
                id: Date.now(),
                name: 'New Conversation',
                messages: [],
                timestamp: new Date().toISOString()
              };
              return [newConversation];
            }
            // Switch to the first conversation in the list
            setCurrentConversationId(updatedConversations[0].id);
            return updatedConversations;
          });
        } catch (error) {
          console.error('Error cleaning up conversation:', error);
        }
        break;
      default:
        break;
    }
    setMenuAnchor(null);
  }, [currentConversationId, handleClearChat, handleNewConversation, cleanupConversation, serverUrl, selectedModel]);

  const handleStopResponse = () => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
      abortControllerRef.current = null;
      setThinking(prev => ({ ...prev, [position]: false }));
      setStreamingResponses(prev => ({ ...prev, [position]: '' }));
    }
  };

  const updateConversationName = (id, firstMessage) => {
    if (!firstMessage) return;

    // Extract first ~30 characters of the first message for the conversation name
    const name = firstMessage.content.length > 30 
      ? firstMessage.content.substring(0, 30) + '...'
      : firstMessage.content;

    setConversations(prev => 
      prev.map(conv => 
        conv.id === id 
          ? { ...conv, name } 
          : conv
      )
    );
  };

  // Batch updates for message list scrolling with longer delay
  useEffect(() => {
    const scrollTimer = setTimeout(() => {
      scrollToBottom();
    }, 1000); // Delay scroll to 1 second

    return () => clearTimeout(scrollTimer);
  }, [currentConversation.messages, streamingResponse, scrollToBottom]);

  // Update streaming response handling
  const StreamingResponseComponent = React.memo(({ content }) => (
    content ? (
      <Message align="left">
        <MessageContent 
          align="left"
          sx={{ 
            backgroundColor: theme => theme.palette.background.paper,
            minWidth: '200px',
            width: 'fit-content',
            maxWidth: '70%'
          }}
        >
          <ReactMarkdown 
            components={{
              p: ({ children }) => (
                <Typography 
                  variant="body1" 
                  component="p" 
                  sx={{ 
                    mb: 1,
                    '&:last-child': { mb: 0 }
                  }}
                >
                  {children}
                </Typography>
              ),
              pre: ({ node, ...props }) => (
                <pre style={{ 
                  backgroundColor: 'rgba(0, 0, 0, 0.1)',
                  padding: '8px',
                  borderRadius: '4px',
                  overflowX: 'auto',
                }} {...props} />
              ),
              code: ({ node, inline, ...props }) => (
                inline ? 
                  <code style={{ 
                    backgroundColor: 'rgba(0, 0, 0, 0.1)',
                    padding: '2px 4px',
                    borderRadius: '3px',
                  }} {...props} /> :
                  <code {...props} />
              ),
            }}
          >
            {content}
          </ReactMarkdown>
        </MessageContent>
      </Message>
    ) : null
  ));

  // Update ThinkingIndicator to be more precise
  const ThinkingIndicator = React.memo(({ isThinking, onStop }) => (
    isThinking && !streamingResponse && !currentConversation.messages.find(m => m.role === 'error') ? (
      <Message align="left">
        <MessageContent 
          align="left" 
          sx={{ 
            backgroundColor: 'rgba(0, 0, 0, 0.2)',
            minWidth: '200px',
            width: 'fit-content',
            maxWidth: '70%',
            position: 'relative',
            padding: '12px 16px',
            '& pre': {
              margin: 0,
              padding: 0,
              backgroundColor: 'transparent',
              fontFamily: 'inherit',
              whiteSpace: 'pre-wrap',
              fontSize: '0.875rem',
            }
          }}
        >
          <Box sx={{ 
            display: 'flex', 
            flexDirection: 'column',
            gap: 1,
          }}>
            <pre>{"<think>"}</pre>
            <pre style={{ marginLeft: '8px' }}>
              {"Processing request and generating response..."}
            </pre>
            <pre>{"</think>"}</pre>
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: 1,
              mt: 1,
              pt: 1,
              borderTop: '1px solid rgba(255, 255, 255, 0.1)',
            }}>
              <CircularProgress size={16} />
              <Typography>Thinking...</Typography>
              <IconButton 
                size="small" 
                onClick={onStop}
                sx={{ 
                  ml: 'auto',
                  bgcolor: 'error.main',
                  color: 'error.contrastText',
                  '&:hover': {
                    bgcolor: 'error.dark',
                  },
                  width: 24,
                  height: 24,
                }}
              >
                <StopIcon sx={{ fontSize: 16 }} />
              </IconButton>
            </Box>
          </Box>
        </MessageContent>
      </Message>
    ) : null
  ));

  // Update model status when a model is successfully used
  const updateModelStatus = useCallback((modelId, isAvailable) => {
    setModelStatus(prev => ({
      ...prev,
      [modelId]: {
        available: isAvailable,
        lastChecked: Date.now()
      }
    }));
  }, []);

  // Check if we need to verify model availability
  const shouldCheckModel = useCallback((modelId) => {
    const status = modelStatus[modelId];
    if (!status) return true;

    const now = Date.now();
    return now - status.lastChecked > networkCheckInterval;
  }, [modelStatus]);

  // Update handleModelAction to preserve models
  const handleModelAction = useCallback(async (action) => {
    if (!selectedModel) return;

    setIsModelSwitching(true);
    try {
      if (action === 'refresh') {
        // Reload the model while preserving others
        await fetch(`${serverUrl}/v1/chat/completions`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            model: selectedModel,
            messages: [{ role: 'system', content: 'test' }],
            stream: false,
            max_tokens: 1,
            options: {
              load_model_only_when_needed: false,
              unload_model_after_completion: false,
              no_auto_model_selection: true,
              skip_model_load_test: true,
              keep_loaded_models: true  // Add this option to prevent unloading
            }
          })
        });
      }
      else if (action === 'unload') {
        // Just clear the UI state without unloading
        setSelectedModel('');
        localStorage.removeItem(`selectedModel-${position}`);
      }
    } catch (error) {
      console.warn(`Failed to ${action} model:`, error);
    } finally {
      setIsModelSwitching(false);
    }
  }, [selectedModel, serverUrl, position]);

  // Add back the missing utility functions
  const getModelColor = (modelName) => {
    if (!modelName) return 'primary.main';
    const name = modelName.trim().toLowerCase();

    // Use a consistent color scheme based on model name
    const colors = {
      hermes: '#00BFA5',    // Teal
      nomic: '#4A90E2',     // Blue
      llama: '#2E7D32',     // Green
      mistral: '#ED6C02',   // Orange
      openchat: '#1976D2',  // Light blue
      qwen: '#9C27B0',      // Purple
      gemma: '#FF4081',     // Pink
      default: '#757575'    // Grey
    };

    // Find the first matching model name in the colors object
    const modelType = Object.keys(colors).find(key => name.includes(key));
    return modelType ? colors[modelType] : colors.default;
  };

  const getModelImage = (modelName) => {
    if (!modelName) return null;
    const name = modelName.trim().toLowerCase();

    // Map of model names to their image files
    const modelImages = {
      hermes: './Images/Hermes.jpg',
      nomic: './Images/Nomic.png',
      qwen: './Images/Qwen2.png',
      starcoder: './Images/StarCoder.jpg',
      gemma: './Images/Gemma.png'
    };

    // Find the first matching model name in the images object
    const modelType = Object.keys(modelImages).find(key => name.includes(key));
    return modelType ? modelImages[modelType] : null;
  };

  const getModelLetter = (modelName) => {
    if (!modelName) return 'A';
    return modelName.charAt(0).toUpperCase();
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSendMessage();
    }
  };

  const handleEmojiClick = (emojiData) => {
    const emoji = emojiData.emoji;
    const cursorPosition = document.querySelector('textarea').selectionStart;
    const updatedText = 
      inputValue.slice(0, cursorPosition) + 
      emoji + 
      inputValue.slice(cursorPosition);
    setInputValue(updatedText);
    setEmojiAnchor(null);
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Reset file input
    event.target.value = '';

    // Check file size (limit to 10MB)
    if (file.size > 10 * 1024 * 1024) {
      setUploadError('File size must be less than 10MB');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('model', selectedModel);

      setThinking(prev => ({ ...prev, [position]: true }));

      const response = await fetch(`${serverUrl}/v1/files`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Upload failed: ${response.statusText}`);
      }

      const data = await response.json();

      // Add file message to chat
      updateConversationMessages([
        ...currentConversation.messages,
        {
          content: `ðŸ“Ž Uploaded file: ${file.name}`,
          timestamp: new Date().toISOString(),
          role: 'user',
          fileId: data.id,
        },
      ]);

    } catch (error) {
      console.error('Upload error:', error);
      setUploadError(error.message);
      updateConversationMessages([
        ...currentConversation.messages,
        {
          content: `Failed to upload file: ${error.message}`,
          timestamp: new Date().toISOString(),
          role: 'error',
        },
      ]);
    } finally {
      setThinking(prev => ({ ...prev, [position]: false }));
    }
  };

  const supportsFileUpload = () => {
    if (!selectedModel || !models.length) return false;
    const currentModel = models.find(m => m.id === selectedModel);
    const modelName = currentModel?.name?.toLowerCase() || '';

    // Enable file uploads for both deepseek and qwen models
    if (modelName.includes('deepseek') || modelName.includes('qwen')) {
      return true;
    }
    // Fallback to checking capabilities if model has them defined
    return currentModel?.capabilities?.includes('file_upload') || false;
  };

  // Add back MessageComponent
  const MessageComponent = memo(({ message, align }) => {
    const [isDragging, setIsDragging] = useState(false);

    const handleDragStart = (e) => {
      setIsDragging(true);
      // Set both text and rich data for different drop targets
      e.dataTransfer.setData('text/plain', message.content);
      e.dataTransfer.setData('application/json', JSON.stringify({
        type: 'chat_message',
        content: message.content,
        timestamp: message.timestamp,
        role: message.role,
        metadata: {
          model: selectedModel,
          modelName: models.find(m => m.id === selectedModel)?.name,
          conversationId: currentConversationId,
          conversationName: currentConversation.name
        }
      }));
      e.dataTransfer.effectAllowed = 'copyMove';

      // Create a custom drag image
      const dragPreview = document.createElement('div');
      dragPreview.className = 'message-drag-preview';
      dragPreview.innerHTML = `
        <div style="
          padding: 8px 12px;
          background: rgba(25, 118, 210, 0.9);
          border-radius: 8px;
          color: white;
          font-size: 14px;
          max-width: 200px;
          overflow: hidden;
          text-overflow: ellipsis;
          white-space: nowrap;
          box-shadow: 0 2px 8px rgba(0,0,0,0.2);
        ">
          ${message.content.substring(0, 50)}${message.content.length > 50 ? '...' : ''}
        </div>
      `;
      document.body.appendChild(dragPreview);
      e.dataTransfer.setDragImage(dragPreview, 0, 0);
      setTimeout(() => document.body.removeChild(dragPreview), 0);

      e.currentTarget.classList.add('dragging');
    };

    const handleDragEnd = (e) => {
      setIsDragging(false);
      e.currentTarget.classList.remove('dragging');
      setTimeout(() => setIsDragging(false), 100);
    };

    return (
      <Message 
        align={align}
        draggable={true}
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd}
        sx={{ 
          cursor: isDragging ? 'grabbing' : 'grab',
          '&:hover': {
            '& .message-content': {
              boxShadow: 2,
              transform: 'translateY(-1px)',
            }
          },
          '&.dragging .message-content': {
            opacity: 0.7,
            boxShadow: 4,
            transform: 'scale(0.98)',
          },
          '& .message-content': {
            transition: 'all 0.2s ease',
          }
        }}
      >
        {message.role === 'error' ? (
          <ErrorMessage>
            <CircularProgress size={16} color="error" />
            <Typography variant="body2">{message.content}</Typography>
          </ErrorMessage>
        ) : (
          <>
            <MessageContent 
              align={align} 
              className="message-content"
              sx={{ 
                transition: 'all 0.2s ease',
              }}
            >
              <ReactMarkdown 
                components={{
                  pre: ({ node, ...props }) => (
                    <pre style={{ 
                      backgroundColor: 'rgba(0, 0, 0, 0.1)',
                      padding: '8px',
                      borderRadius: '4px',
                      overflowX: 'auto',
                    }} {...props} />
                  ),
                  code: ({ node, inline, ...props }) => (
                    inline ? 
                      <code style={{ 
                        backgroundColor: 'rgba(0, 0, 0, 0.1)',
                        padding: '2px 4px',
                        borderRadius: '3px',
                      }} {...props} /> :
                      <code {...props} />
                  ),
                }}
              >
                {message.content}
              </ReactMarkdown>
            </MessageContent>
            <Typography 
              variant="caption" 
              color="textSecondary" 
              sx={{ 
                mt: 0.5,
                opacity: 0.7,
                fontSize: '0.7rem',
              }}
            >
              {new Date(message.timestamp).toLocaleTimeString()}
            </Typography>
          </>
        )}
      </Message>
    );
  });

  // Save brainstorm state
  useEffect(() => {
    localStorage.setItem(`brainstorm-enabled-${position}`, brainstormEnabled);
  }, [brainstormEnabled, position]);

  // Save brainstorm iterations to localStorage
  useEffect(() => {
    localStorage.setItem(`brainstorm-iterations-${position}`, brainstormIterations);
  }, [brainstormIterations, position]);

  const handleEditName = () => {
    setEditedName(currentConversation.name);
    setIsEditingName(true);
  };

  const handleSaveName = () => {
    if (editedName.trim()) {
      setConversations(prev => prev.map(conv => 
        conv.id === currentConversationId 
          ? { ...conv, name: editedName.trim() }
          : conv
      ));
    }
    setIsEditingName(false);
  };

  const handleCancelEdit = () => {
    setIsEditingName(false);
    setEditedName('');
  };

  // Add brainstorm menu handler
  const handleBrainstormContextMenu = (event) => {
    event.preventDefault();
    setBrainstormMenuAnchor(event.currentTarget);
  };

  // Update the brainstorm menu items to save state
  const handleBrainstormIterationChange = (count) => {
    setBrainstormIterations(count);
    setBrainstormMenuAnchor(null);
    // If iterations are set but brainstorm is not enabled, enable it
    if (!brainstormEnabled) {
      setBrainstormEnabled(true);
    }
  };

  const handleDownloadBrainstorm = useCallback(() => {
    const formatMessage = (msg) => {
      const timestamp = new Date(msg.timestamp).toLocaleString();
      const model = msg.metadata?.modelName || 'Unknown Model';
      const role = msg.role === 'user' ? 'Human' : 'Assistant';
      return `[${timestamp}] ${role} (${model}):\n${msg.content}\n\n`;
    };

    const text = currentConversation.messages.map(formatMessage).join('');
    const blob = new Blob([text], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `conversation-${currentConversation.name}-${new Date().toISOString()}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  }, [currentConversation]);

  // Add the missing handleBrainstormToggle function
  const handleBrainstormToggle = useCallback(() => {
    const newState = !brainstormEnabled;
    setBrainstormEnabled(newState);

    // Notify other panel
    const event = new CustomEvent('brainstormSync', {
      detail: {
        enabled: newState,
        iterations: brainstormIterations,
        fromPosition: position
      }
    });
    window.dispatchEvent(event);
  }, [brainstormEnabled, brainstormIterations, position]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!inputValue.trim() || !selectedModel || isModelSwitching) return;

    const message = inputValue.trim();
    setInputValue('');
    onMessageSubmit?.(message);  // Call onMessageSubmit with the message

    // Update conversation with user message
    const userMessage = {
      role: 'user',
      content: message,
      timestamp: new Date().toISOString(),
      metadata: {
        model: selectedModel,
        modelName: models.find(m => m.id === selectedModel)?.name || 'Unknown Model'
      }
    };

    // ... rest of the existing handleSubmit code ...
  };

  return (
    <StyledPaper elevation={3}>
      <ChatHeader>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Tooltip title={selectedModel ? (models.find(m => m.id === selectedModel)?.name || 'Select a model') : 'Select a model'}>
            <Avatar sx={{ 
              bgcolor: selectedModel ? 
                getModelColor(models.find(m => m.id === selectedModel)?.name || '') :
                'primary.main',
              transition: 'all 0.3s ease',
              fontWeight: 600,
              width: 40,
              height: 40,
              cursor: 'help',
              '& img': {
                width: '100%',
                height: '100%',
                objectFit: 'cover',
              }
            }}>
              {selectedModel ? (
                (() => {
                  const modelName = models.find(m => m.id === selectedModel)?.name || '';
                  const imagePath = getModelImage(modelName);
                  return imagePath ? (
                    <img 
                      src={imagePath} 
                      alt={modelName}
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover',
                      }}
                    />
                  ) : (
                    getModelLetter(modelName)
                  );
                })()
              ) : 'A'}
            </Avatar>
          </Tooltip>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
            {models.length > 0 ? (
              <ModelSelector size="small" sx={{ minWidth: 200 }}>
                <Select
                  value={selectedModel || ''}
                  onChange={(e) => handleModelChange(e.target.value)}
                  variant="standard"
                  displayEmpty
                >
                  {models.map((model) => (
                    <MenuItem key={model.id} value={model.id}>
                      {model.name}
                    </MenuItem>
                  ))}
                </Select>
              </ModelSelector>
            ) : (
              <Typography variant="caption" color="error">
                No AI models available
              </Typography>
            )}
          </Box>
        </Box>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Tooltip title={brainstormEnabled ? "AI Brainstorm Active" : "Enable AI Brainstorm\nRight-click to set iterations"}>
            <IconButton 
              onClick={handleBrainstormToggle}
              onContextMenu={handleBrainstormContextMenu}
              color={brainstormEnabled ? "primary" : "default"}
              sx={{
                position: 'relative',
                '&::after': brainstormEnabled ? {
                  content: '""',
                  position: 'absolute',
                  top: '50%',
                  left: '50%',
                  width: '120%',
                  height: '120%',
                  borderRadius: '50%',
                  transform: 'translate(-50%, -50%)',
                  animation: 'pulse 2s infinite',
                  backgroundColor: 'primary.main',
                  opacity: 0.2,
                } : {},
                '@keyframes pulse': {
                  '0%': {
                    transform: 'translate(-50%, -50%) scale(0.95)',
                    opacity: 0.5,
                  },
                  '70%': {
                    transform: 'translate(-50%, -50%) scale(1.1)',
                    opacity: 0.2,
                  },
                  '100%': {
                    transform: 'translate(-50%, -50%) scale(0.95)',
                    opacity: 0.5,
                  },
                },
              }}
            >
              <BrainstormIcon />
            </IconButton>
          </Tooltip>
          <IconButton 
            onClick={handleNewConversation}
            title="New Conversation"
          >
            <AddIcon />
          </IconButton>
          <IconButton onClick={(e) => setMenuAnchor(e.currentTarget)}>
            <MoreIcon />
          </IconButton>
        </Box>
      </ChatHeader>

      <MessageList
        ref={messageListRef}
        onScroll={(e) => {
          if (isScrolledToBottom()) {
            setUserHasScrolled(false);
          }
        }}
      >
        {currentConversation?.messages?.map((msg, index) => (
          <MessageComponent
            key={`${msg.timestamp}-${index}-${msg.content.substring(0, 20)}`}
            message={msg}
            align={msg.role === 'user' ? 'right' : 'left'}
          />
        ))}
        {streamingResponse && (
          <MessageComponent
            message={{
              content: streamingResponse,
              timestamp: new Date().toISOString(),
              role: 'assistant'
            }}
            align="left"
          />
        )}
        {isThinking && (
          <ThinkingIndicator 
            isThinking={isThinking} 
            onStop={handleStopResponse}
          />
        )}
        <div ref={messagesEndRef} />
      </MessageList>

      <ChatFooter>
        <InputContainer
          onDragOver={(e) => {
            e.preventDefault();
            e.currentTarget.style.backgroundColor = 'rgba(25, 118, 210, 0.1)';
            e.dataTransfer.dropEffect = 'copy';
          }}
          onDragLeave={(e) => {
            e.preventDefault();
            e.currentTarget.style.backgroundColor = '';
          }}
          onDrop={(e) => {
            e.preventDefault();
            e.currentTarget.style.backgroundColor = '';
            const text = e.dataTransfer.getData('text/plain');
            if (text) {
              setInputValue(prev => {
                const textarea = document.querySelector('textarea');
                const cursorPosition = textarea?.selectionStart || prev.length;
                return prev.slice(0, cursorPosition) + text + prev.slice(cursorPosition);
              });
              // Focus and scroll to end
              const textarea = document.querySelector('textarea');
              if (textarea) {
                textarea.focus();
                textarea.scrollTop = textarea.scrollHeight;
              }
            }
          }}
          sx={{
            transition: 'all 0.2s ease',
            '&:hover': {
              '&[data-dragging="true"]': {
                backgroundColor: 'rgba(25, 118, 210, 0.1)',
              }
            }
          }}
        >
          <IconButton 
            size="small" 
            onClick={(e) => setEmojiAnchor(e.currentTarget)}
          >
            <EmojiIcon />
          </IconButton>
          <Popover
            open={Boolean(emojiAnchor)}
            anchorEl={emojiAnchor}
            onClose={() => setEmojiAnchor(null)}
            anchorOrigin={{
              vertical: 'top',
              horizontal: 'left',
            }}
            transformOrigin={{
              vertical: 'bottom',
              horizontal: 'left',
            }}
          >
            <Box sx={{ 
              '.EmojiPickerReact': {
                '--epr-bg-color': 'rgba(18, 18, 18, 0.95)',
                '--epr-category-label-bg-color': 'rgba(18, 18, 18, 0.95)',
                '--epr-hover-bg-color': 'rgba(255, 255, 255, 0.1)',
                '--epr-focus-bg-color': 'rgba(255, 255, 255, 0.1)',
                '--epr-highlight-color': 'rgba(255, 255, 255, 0.2)',
                '--epr-search-border-color': 'rgba(255, 255, 255, 0.1)',
                '--epr-border-color': 'rgba(255, 255, 255, 0.1)',
                '--epr-text-color': '#fff',
                border: 'none',
                boxShadow: '0 4px 20px rgba(0, 0, 0, 0.3)',
              }
            }}>
              <EmojiPicker
