import React, { useState } from 'react';
import { Box, IconButton, Paper, Tooltip } from '@mui/material';
import { styled } from '@mui/material/styles';
import { Add as AddIcon, Close as CloseIcon } from '@mui/icons-material';
import { Resizable } from 're-resizable';

const PanelContainer = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'row',
  height: 'calc(100vh - 16px)',
  width: '100%',
  overflow: 'hidden',
  padding: theme.spacing(1),
}));

const Panel = styled(Paper)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  height: '100%',
  flex: 1,
  minWidth: 300,
  position: 'relative',
  overflow: 'hidden',
  backgroundColor: theme.palette.background.default,
}));

const PanelHeader = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(1, 2),
  borderBottom: `1px solid ${theme.palette.divider}`,
  backgroundColor: theme.palette.background.paper,
  userSelect: 'none',
}));

const PanelContent = styled(Box)({
  flex: 1,
  overflow: 'auto',
  position: 'relative',
  height: 'calc(100% - 48px)', // Account for header height
});

const AddPanelButton = styled(IconButton)(({ theme }) => ({
  position: 'fixed',
  bottom: theme.spacing(2),
  right: theme.spacing(2),
  zIndex: theme.zIndex.drawer + 1,
}));

function PanelLayout({ children }) {
  const [panels, setPanels] = useState([
    { id: 'panel-1', width: '50%' },
    { id: 'panel-2', width: '50%' }
  ]);

  const handleResize = (index, delta) => {
    setPanels(prev => {
      const newPanels = [...prev];
      const containerWidth = document.querySelector('.panel-container').offsetWidth;
      
      // Convert current width from percentage to pixels
      const currentWidth = parseFloat(newPanels[index].width);
      const currentPixels = (currentWidth / 100) * containerWidth;
      
      // Calculate total width of all panels in pixels
      const totalPixels = newPanels.reduce((sum, panel) => {
        return sum + (parseFloat(panel.width) / 100) * containerWidth;
      }, 0);
      
      // Calculate new width in pixels, ensuring it doesn't cause overflow
      const maxAllowedDelta = containerWidth - totalPixels;
      const constrainedDelta = Math.min(delta, maxAllowedDelta);
      const newPixels = currentPixels + constrainedDelta;
      
      // Convert back to percentage, ensuring it stays within bounds
      const newPercentage = Math.max(
        Math.min((newPixels / containerWidth) * 100, 80),
        (300 / containerWidth) * 100
      );
      
      // Update only the resized panel's width
      newPanels[index] = {
        ...newPanels[index],
        width: `${newPercentage}%`
      };
      
      return newPanels;
    });
  };

  const handleRemovePanel = (panelId) => {
    if (panels.length > 1) {
      setPanels(prev => {
        const newPanels = prev.filter(p => p.id !== panelId);
        const newWidth = `${100 / newPanels.length}%`;
        return newPanels.map(p => ({ ...p, width: newWidth }));
      });
    }
  };

  const handleAddNewPanel = () => {
    setPanels(prev => {
      const newWidth = `${100 / (prev.length + 1)}%`;
      return [
        ...prev.map(p => ({ ...p, width: newWidth })),
        { id: `panel-${prev.length + 1}`, width: newWidth }
      ];
    });
  };

  return (
    <>
      <PanelContainer className="panel-container">
        {panels.map((panel, index) => (
          <Resizable
            key={panel.id}
            size={{ width: panel.width, height: '100%' }}
            onResize={(e, direction, ref, delta) => {
              handleResize(index, delta.width);
            }}
            enable={{
              right: index < panels.length - 1,
              left: false,
            }}
            minWidth={300}
            maxWidth="80%"
            handleComponent={{
              right: (
                <Box
                  sx={{
                    position: 'absolute',
                    right: -4,
                    top: 0,
                    bottom: 0,
                    width: 8,
                    cursor: 'col-resize',
                    zIndex: 1,
                    '&:hover': {
                      backgroundColor: 'action.hover',
                    },
                    '&::after': {
                      content: '""',
                      position: 'absolute',
                      left: '50%',
                      width: 2,
                      top: 0,
                      bottom: 0,
                      backgroundColor: 'divider',
                    },
                  }}
                />
              ),
            }}
          >
            <Panel elevation={2}>
              <PanelHeader>
                Chat Window {index + 1}
                {panels.length > 1 && (
                  <IconButton
                    size="small"
                    onClick={() => handleRemovePanel(panel.id)}
                    sx={{ ml: 'auto' }}
                  >
                    <CloseIcon fontSize="small" />
                  </IconButton>
                )}
              </PanelHeader>
              <PanelContent>
                {typeof children === 'function' ? children(panel.id) : children}
              </PanelContent>
            </Panel>
          </Resizable>
        ))}
      </PanelContainer>
      
      <Tooltip title="Add new chat window">
        <AddPanelButton
          color="primary"
          onClick={handleAddNewPanel}
          size="large"
        >
          <AddIcon />
        </AddPanelButton>
      </Tooltip>
    </>
  );
}

export default PanelLayout;
